64     /// Database URL
65     /// </summary>
66     public string Url { get; protected internal set; } = "";
67 
68     /// <summary>
69     /// Collection of retrieved DocumentObjects stored in a dictionary with key universalID of the document
70     /// </summary>
71     public Dictionary<string, DocumentObject> Documents { get; protected internal set; } = null;
