90     /// <summary>
91     /// Database constructor method
92     /// </summary>
93     /// <param name="session"></param>
94     /// <param name="replicationID"></param>
95     /// <param name="server">The server name abbreviated (ANTLN-TEST/ANTWERPEN/JacobsEngineering) </param>
96     public DatabaseObject(SessionObject session, string replicationID, string server) {
97         Session = session;
98         ReplicationID = replicationID;
99         ServerName = server;
100     }
101 
102     /// <summary>
103     /// Database constructor method via session with initialization string.
104     /// This will not reset the Connector ReturnMessages
105     /// </summary>
106     /// <param name="session"></param>
107     /// <param name="initString"></param>
108     internal DatabaseObject(SessionObject session, string initString) {
109         
110         if(session != null && session.IsInitialized) {
111             Session = session;
112             if (!String.IsNullOrEmpty(initString)) {
113                 // FilePath§ServerName§ReplicationID§Title§Template§Size§URL
114                 if (initString.Contains("§")) {
115                     String[] ar = initString.Split(new[] { "§" }, StringSplitOptions.None);
116                     if (ar != null && ar.Length > 0 && ar.Length == 7) {
117                         FilePath = ar[0];
118                         ServerName = ar[1];
119                         ReplicationID = ar[2];
120                         Title = ar[3];
121                         Template = ar[4];
122                         Size = ar[5];
123                         Url = ar[6];
124                         IsInitialized = true;
125                     } else {
126                         Connector.ReturnMessages.Add("Unable to create database object - Initialization String Not Validated");
127                         IsInitialized = false;
128                         Connector.hasError = true;
129                     }
130                 } else {
131                     Connector.ReturnMessages.Add("Unable to create database object - Initialization String Not Valid");
132                     IsInitialized = false;
133                     Connector.hasError = true;
134                 }
135             } else {
136                 Connector.ReturnMessages.Add("Unable to create database object - Initialization String Not Provided");
137                 IsInitialized = false;
138                 Connector.hasError = true;
139             }
140         } else {
141             // session is not valid
142             Connector.ReturnMessages.Add("Unable to create database object - Session Object Not Valid");
143             IsInitialized = false;
144             Connector.hasError = true;
145         }
146       
147     }
148 
149     #endregion
