153     /// <summary>
154     ///  Initializes the DatabaseObject by validating the input and triggering the database request
155     /// </summary>
156     /// <returns>Boolean</returns>
157     public bool Initialize() {
158 
159         Connector.ResetReturn();
160         // reset the list of docs
161         Documents = null;
162 
163         if (!ValidateInput()) {
164             IsInitialized = false;
165             Connector.hasError = true;
166             return false;   // throws exception
167         }
168 
169         // make a connection to the webservice database - this will check the users authentication on that database
170         if (Session.Connection.Request.ExecuteDatabaseRequest(Session.WebServiceURL, ServerName, FilePath, ReplicationID, this)) {
171             IsInitialized = true;
172             Connector.hasError = false;
173             return true;
174         } else {
175             //error messages written to Connection.ReturnMessages by Connection.Request.ExecuteSessionRequest
176             IsInitialized = false;
177             return false;
178         }
179                 
180     }
181 
182     /// <summary>
183     /// Validate the input provided by the user
184     /// </summary>
185     /// <returns>Boolean</returns>
186     private bool ValidateInput() {
187         if (Session == null || !Session.IsInitialized) {
188             //can only be initialized if valid connector and connector is initialized and connected
189             Connector.ReturnMessages.Add("DatabaseObject can not be validated : Session is not initialized! (DatabaseObject.ValidateInput)");
190             return false;
191         }
192         //check server, dbrep or filepath
193         if (string.IsNullOrEmpty(ServerName)) {
194             Connector.ReturnMessages.Add("DatabaseObject can not be validated : ServerName has not been provided! (DatabaseObject.ValidateInput)");
195             return false;
196         }
197 
198         if (string.IsNullOrEmpty(FilePath)) {
199             //we must have a rep id
200             if (string.IsNullOrEmpty(ReplicationID)) {
201                 Connector.ReturnMessages.Add("DatabaseObject can not be validated : FilePath/ReplicationID has not been provided! (DatabaseObject.ValidateInput)");
202                 return false;
203             }
204         }
205         return true;
206     }
207 
208     /// <summary>
209     /// Retrieve a document from this database by universalID
210     /// <para>Triggers a document request</para>
211     /// <para>The document will be added to the property 'Documents'</para>
212     /// </summary>
213     /// <param name="unid"></param>
214     /// <returns>DocumentObject</returns>
215     public DocumentObject GetDocument(string unid) {
216         Connector.ResetReturn();
217         DocumentObject docObj = null;
218         if (IsInitialized) {
219             docObj = new DocumentObject(this, unid);
220             if  (docObj.Initialize()) {
221                 return docObj;
222             } else {
223                 return null;
224             }
225         }
226         return docObj;
227     }
