229     /// <summary>
230     /// Retrieve a document from this database by universalID
231     /// This function will, in addition to retrieving the default document information, retrieve the associated domino attachment file objects
232     /// <para>Triggers a document request</para>
233     /// <para>The document will be added to the property 'Documents'</para>
234     /// </summary>
235     /// <param name="unid"></param>
236     /// <returns>DocumentObject</returns>
237     public DocumentObject GetDocumentAndFiles(string unid)
238     {
239         Connector.ResetReturn();
240         DocumentObject docObj = null;
241         if (IsInitialized)
242         {
243             docObj = new DocumentObject(this, unid);
244             if (docObj.InitializeWithFiles())
245             {
246                 return docObj;
247             }
248             else
249             {
250                 return null;
251             }
252         }
253         return docObj;
254     }
