585     /// <summary>
586     /// Retrieve fields for all documents in the property 'Documents'
587     /// <para>Provide the fields separated by ;</para>
588     /// <para>This action will update the property collection Fields inside each document object</para>
589     /// </summary>
590     /// <param name="listFields"></param>
591     /// <returns>Boolean</returns>
592     public bool LoadDocumentFields(string listFields) {
593         Connector.ResetReturn();
594         if (IsInitialized && Documents != null && Documents.Count > 0) {
595             if (Session.Connection.Request.ExecuteAllFieldsRequest(Session.WebServiceURL,this,listFields)) {
596                 return true;
597             } else {
598                 return false;
599             }
600         }
601         return true;
602     }
