604     /// <summary>
605     /// Retrieve fields for all documents in the property 'Documents'
606     /// <para>This action will update the property collection Fields inside each document object</para>
607     /// </summary>
608     /// <param name="listFields"></param>
609     /// <returns>Boolean</returns>
610     public bool LoadDocumentFields(IList listFields) {
611         Connector.ResetReturn();
612         if (IsInitialized && Documents != null && Documents.Count > 0) {
613             string fields = Common.GetListAsString(listFields, ";");
614             if (Session.Connection.Request.ExecuteAllFieldsRequest(Session.WebServiceURL, this, fields)) {
