88     /// <summary>
89     /// Database constructor method
90     /// </summary>
91     /// <param name="session"></param>
92     /// <param name="replicationID"></param>
93     /// <param name="server">The server name abbreviated (ANTLN-TEST/ANTWERPEN/JacobsEngineering) </param>
94     public DatabaseObject(SessionObject session, string replicationID, string server) {
95         Session = session;
96         ReplicationID = replicationID;
97         ServerName = server;
98     }
99 
100     /// <summary>
101     /// Database constructor method via session with initialization string.
102     /// This will not reset the Connector ReturnMessages
103     /// </summary>
104     /// <param name="session"></param>
105     /// <param name="initString"></param>
106     internal DatabaseObject(SessionObject session, string initString) {
107         if (session != null && session.IsInitialized) {
108             Session = session;
109             if (!String.IsNullOrEmpty(initString)) {
110                 // FilePath§ServerName§ReplicationID§Title§Template§Size§URL
111                 if (initString.Contains("§")) {
112                     String[] ar = initString.Split(new[] { "§" }, StringSplitOptions.None);
113                     if (ar != null && ar.Length > 0 && ar.Length == 7) {
114                         FilePath = ar[0];
115                         ServerName = ar[1];
116                         ReplicationID = ar[2];
117                         Title = ar[3];
118                         Template = ar[4];
119                         Size = ar[5];
120                         Url = ar[6];
121                         IsInitialized = true;
122                     } else {
123                         Connector.ReturnMessages.Add("Unable to create database object - Initialization String Not Validated");
124                         IsInitialized = false;
125                         Connector.HasError = true;
126                     }
127                 } else {
128                     Connector.ReturnMessages.Add("Unable to create database object - Initialization String Not Valid");
129                     IsInitialized = false;
130                     Connector.HasError = true;
131                 }
132             } else {
133                 Connector.ReturnMessages.Add("Unable to create database object - Initialization String Not Provided");
134                 IsInitialized = false;
135                 Connector.HasError = true;
136             }
137         } else {
138             // session is not valid
139             Connector.ReturnMessages.Add("Unable to create database object - Session Object Not Valid");
140             IsInitialized = false;
141             Connector.HasError = true;
142         }
143     }
144 
145     #endregion Constructors
