149     /// <summary>
150     ///  Initializes the DatabaseObject by validating the input and triggering the database request
151     /// </summary>
152     /// <returns>Boolean</returns>
153     public bool Initialize() {
154         Connector.ResetReturn();
155         // reset the list of docs
156         Documents = null;
157 
158         if (!ValidateInput()) {
159             IsInitialized = false;
160             Connector.HasError = true;
161             return false;   // throws exception
162         }
163 
164         // make a connection to the webservice database - this will check the users authentication on that database
165         if (Session.Connection.Request.ExecuteDatabaseRequest(Session.WebServiceURL, ServerName, FilePath, ReplicationID, this)) {
166             IsInitialized = true;
167             Connector.HasError = false;
168             return true;
169         } else {
170             //error messages written to Connection.ReturnMessages by Connection.Request.ExecuteSessionRequest
171             IsInitialized = false;
172             return false;
173         }
174     }
175 
176     /// <summary>
177     /// Validate the input provided by the user
178     /// </summary>
179     /// <returns>Boolean</returns>
180     private bool ValidateInput() {
181         if (Session == null || !Session.IsInitialized) {
182             //can only be initialized if valid connector and connector is initialized and connected
183             Connector.ReturnMessages.Add("DatabaseObject can not be validated : Session is not initialized! (DatabaseObject.ValidateInput)");
184             return false;
185         }
186         //check server, dbrep or filepath
187         if (string.IsNullOrEmpty(ServerName)) {
188             Connector.ReturnMessages.Add("DatabaseObject can not be validated : ServerName has not been provided! (DatabaseObject.ValidateInput)");
189             return false;
190         }
191 
192         if (string.IsNullOrEmpty(FilePath)) {
193             //we must have a rep id
194             if (string.IsNullOrEmpty(ReplicationID)) {
195                 Connector.ReturnMessages.Add("DatabaseObject can not be validated : FilePath/ReplicationID has not been provided! (DatabaseObject.ValidateInput)");
196                 return false;
197             }
198         }
199         return true;
200     }
