202     /// <summary>
203     /// Retrieve a document from this database by universalID
204     /// <para>Triggers a document request</para>
205     /// <para>The document will be added to the property 'Documents'</para>
206     /// </summary>
207     /// <param name="unid"></param>
208     /// <returns>DocumentObject</returns>
209     public DocumentObject GetDocument(string unid) {
210         Connector.ResetReturn();
211         DocumentObject docObj = null;
212         if (IsInitialized) {
213             docObj = new DocumentObject(this, unid);
214             if (docObj.Initialize()) {
215                 return docObj;
216             } else {
217                 return null;
218             }
219         }
220         return docObj;
221     }
