687     /// <para>All documents found will be added to the property 'Documents'</para>
688     /// </summary>
689     /// <param name="listUnids"></param>
690     /// <returns>Boolean</returns>
691     public bool GetAllDocumentsByUnids(IList listUnids) {
692         Connector.ResetReturn();
693         if (IsInitialized) {
694             string unids = Common.GetListAsString(listUnids, ";");
695 
696             if (Session.Connection.Request.ExecuteAllDocumentsRequest(Session.WebServiceURL, null, null, null, unids, this)) {
697                 return true;
698             } else {
699                 return false;
700             }
701         }
702         return false;
703     }
