857     /// <summary>
858     /// Retrieve fields for all documents in the property 'Documents'
859     /// <para>Provide the fields separated by ;</para>
860     /// <para>This action will update the property collection Fields inside each document object</para>
861     /// </summary>
862     /// <param name="listFields"></param>
863     /// <returns>Boolean</returns>
864     public bool LoadDocumentFields(string listFields) {
865         Connector.ResetReturn();
866         if (IsInitialized && Documents != null && Documents.Count > 0) {
867             if (Session.Connection.Request.ExecuteAllFieldsRequest(Session.WebServiceURL, this, listFields)) {
868                 return true;
869             } else {
870                 return false;
871             }
872         }
873         return true;
874     }
