5 /// <summary>
6 /// An object representing a domino database object
7 /// </summary>
8 /// <example>The following is an example of the usage of a
9 /// <c>DatabaseObject</c>:
10 ///   <code>
11 ///    DatabaseObject dbObj = new DatabaseObject(filePath, dominoServerName,  sessionObject); // by FilePath
12 ///    //  DatabaseObject dbObj = new DatabaseObject(sessionObject, replicationID, dominoServerName); // by ReplicationID
13 ///    if(dbObj!=null &amp;&amp; dbObj.Initialize()){
14 ///         // your code here...
15 ///    }
16 ///   </code>
17 /// </example>
18 public class DatabaseObject {
19 
