88     /// <summary>
89     /// Database constructor method
90     /// </summary>
91     /// <param name="session"></param>
92     /// <param name="replicationID"></param>
93     /// <param name="server">The server name abbreviated (ANTLN-TEST/ANTWERPEN/JacobsEngineering) </param>
94     public DatabaseObject(SessionObject session, string replicationID, string server) {
95         Session = session;
96         ReplicationID = replicationID;
97         ServerName = server;
98     }
