538     /// <summary>
539     /// Retrieve fields for all documents in the property 'Documents'
540     /// <para>Provide the fields separated by ;</para>
541     /// <para>This action will update the property collection Fields inside each document object</para>
542     /// </summary>
543     /// <param name="listFields"></param>
544     /// <returns>Boolean</returns>
545     public bool LoadDocumentFields(string listFields) {
546         Connector.ResetReturn();
547         if (IsInitialized && Documents != null && Documents.Count > 0) {
548             if (Session.Connection.Request.ExecuteAllFieldsRequest(Session.WebServiceURL, this, listFields)) {
549                 return true;
550             } else {
551                 return false;
552             }
553         }
554         return true;
555     }
