191     /// <summary>
192     ///  Initializes the DatabaseObject by validating the input and triggering the database request
193     /// </summary>
194     /// <returns>Boolean</returns>
195     public bool Initialize() {
196 
197         Connector.ResetReturn();
198         // reset the list of docs
199         _Documents = null;
200 
201         if (!ValidateInput()) {
202             _isInitialized = false;
203             Connector.hasError = true;
204             return false;   // throws exception
205         }
206 
207         // make a connection to the webservice database - this will check the users authentication on that database
208         if (_Session.Connection.Request.ExecuteDatabaseRequest(_Session.WebServiceURL, _ServerName, _FilePath, _ReplicationID, this)) {
209             _isInitialized = true;
210             Connector.hasError = false;
211             return true;
212         } else {
213             //error messages written to Connection.ReturnMessages by Connection.Request.ExecuteSessionRequest
214             _isInitialized = false;
215             return false;
216         }
217                 
218     }
219 
220     /// <summary>
221     /// Validate the input provided by the user
222     /// </summary>
223     /// <returns>Boolean</returns>
224     private bool ValidateInput() {
225         if (_Session == null || !_Session.IsInitialized) {
226             //can only be initialized if valid connector and connector is initialized and connected
227             Connector.ReturnMessages.Add("DatabaseObject can not be validated : Session is not initialized! (DatabaseObject.ValidateInput)");
228             return false;
229         }
230         //check server, dbrep or filepath
231         if (string.IsNullOrEmpty(_ServerName)) {
232             Connector.ReturnMessages.Add("DatabaseObject can not be validated : ServerName has not been provided! (DatabaseObject.ValidateInput)");
233             return false;
234         }
235 
236         if (string.IsNullOrEmpty(_FilePath)) {
237             //we must have a rep id
238             if (string.IsNullOrEmpty(_ReplicationID)) {
239                 Connector.ReturnMessages.Add("DatabaseObject can not be validated : FilePath/ReplicationID has not been provided! (DatabaseObject.ValidateInput)");
240                 return false;
241             }
242         }
243         return true;
244     }
245 
246     /// <summary>
247     /// Retrieve a document from this database by universalID
248     /// <para>Triggers a document request</para>
249     /// <para>The document will be added to the property 'Documents'</para>
250     /// </summary>
251     /// <param name="unid"></param>
252     /// <returns>DocumentObject</returns>
253     public DocumentObject GetDocument(string unid) {
254         Connector.ResetReturn();
255         DocumentObject docObj = null;
256         if (_isInitialized) {
257             docObj = new DocumentObject(this, unid);
258             if  (docObj.Initialize()) {
259                 return docObj;
260             } else {
261                 return null;
262             }
263         }
264         return docObj;
265     }
266 
267     /// <summary>
268     /// Retrieve a document from this database by searching for a specific value in the provided field.
269     /// <para>The first document found will be returned</para>
270     /// <para>Triggers a document request</para>
271     /// <para>The document will be added to the property 'Documents'</para>
272     /// </summary>
273     /// <param name="searchField"></param>
274     /// <param name="searchValue"></param>
275     /// <returns>DocumentObject</returns>
276     public DocumentObject GetDocumentByKey(string searchField, string searchValue) {
277         Connector.ResetReturn();
278         DocumentObject docObj = null;
279         if (_isInitialized) {
280             docObj = new DocumentObject(this, searchField,searchValue);
281             if (docObj.Initialize()) {
282                 return docObj;
283             } else {
284                 return null;
285             }
286         }
287         return docObj;
288     }
