290     /// <summary>
291     /// Retrieve a document from this database by searching using the provided formula.
292     /// <para>The first document found will be returned</para>
293     /// <para>Triggers a document request</para>
294     /// <para>The document will be added to the property 'Documents'</para>
295     /// </summary>
296     /// <param name="formula"></param>
297     /// <returns>DocumentObject</returns>
298     public DocumentObject GetDocumentByFormula(string formula) {
299         Connector.ResetReturn();
300         DocumentObject docObj = null;
301         if (_isInitialized) {
302             docObj = new DocumentObject(formula,this);
303             if (docObj.Initialize()) {
304                 return docObj;
305             } else {
306                 return null;
307             }
308         }
309         return docObj;
310     }
