312     /// <summary>
313     /// Retrieve all documents from the database
314     /// <para>Triggers an all documents request</para>
315     /// <para>All documents found will be added to the property 'Documents'</para>
316     /// </summary>
317     /// <returns>Boolean</returns>
318     public bool GetAllDocuments() {
319         Connector.ResetReturn();
320         if (_isInitialized) {
321             if (_Session.Connection.Request.ExecuteAllDocumentsRequest(_Session.WebServiceURL, null, null, null, null, this)) {
322                 return true;
323             } else {
324                 return false;
325             }
326         }
327         return false;
328     }
