372     /// <para>Triggers an all documents request</para>
373     /// <para>All documents found will be added to the property 'Documents'</para>
374     /// </summary>
375     /// <param name="listUnids"></param>
376     /// <returns>Boolean</returns>
377     public bool GetAllDocumentsByUnids(IList listUnids) {
378         Connector.ResetReturn();
379         if (_isInitialized) {
380             string unids = Common.GetListAsString(listUnids, ";");
381 
382             if (_Session.Connection.Request.ExecuteAllDocumentsRequest(_Session.WebServiceURL, null, null, null, unids, this)) {
