412     /// <summary>
413     /// Retrieve fields for all documents in the property 'Documents'
414     /// <para>Provide the fields separated by ;</para>
415     /// <para>This action will update the property collection Fields inside each document object</para>
416     /// </summary>
417     /// <param name="listFields"></param>
418     /// <returns>Boolean</returns>
419     public bool LoadDocumentFields(string listFields) {
420         Connector.ResetReturn();
421         if (_isInitialized && _Documents != null && _Documents.Count > 0) {
422             if (_Session.Connection.Request.ExecuteAllFieldsRequest(_Session.WebServiceURL,this,listFields)) {
423                 return true;
424             } else {
425                 return false;
426             }
427         }
428         return true;
429     }
430 
431     /// <summary>
432     /// Retrieve fields for all documents in the property 'Documents'
433     /// <para>This action will update the property collection Fields inside each document object</para>
434     /// </summary>
435     /// <param name="listFields"></param>
436     /// <returns>Boolean</returns>
437     public bool LoadDocumentFields(IList listFields) {
438         Connector.ResetReturn();
439         if (_isInitialized && _Documents != null && _Documents.Count > 0) {
440             string fields = Common.GetListAsString(listFields, ";");
441             if (_Session.Connection.Request.ExecuteAllFieldsRequest(_Session.WebServiceURL, this, fields)) {
442                 return true;
443             } else {
444                 return false;
445             }
446         }
447         return true;
448     }
