193     /// <summary>
194     ///  Initializes the DatabaseObject by validating the input and triggering the database request
195     /// </summary>
196     /// <returns>Boolean</returns>
197     public bool Initialize() {
198 
199         Connector.ResetReturn();
200         // reset the list of docs
201         _Documents = null;
202 
203         if (!ValidateInput()) {
204             _isInitialized = false;
205             Connector.hasError = true;
206             return false;   // throws exception
207         }
208 
209         // make a connection to the webservice database - this will check the users authentication on that database
210         if (_Session.Connection.Request.ExecuteDatabaseRequest(_Session.WebServiceURL, _ServerName, _FilePath, _ReplicationID, this)) {
211             _isInitialized = true;
212             Connector.hasError = false;
213             return true;
214         } else {
215             //error messages written to Connection.ReturnMessages by Connection.Request.ExecuteSessionRequest
216             _isInitialized = false;
217             return false;
218         }
219                 
220     }
221 
222     /// <summary>
223     /// Validate the input provided by the user
224     /// </summary>
225     /// <returns>Boolean</returns>
226     private bool ValidateInput() {
227         if (_Session == null || !_Session.IsInitialized) {
228             //can only be initialized if valid connector and connector is initialized and connected
229             Connector.ReturnMessages.Add("DatabaseObject can not be validated : Session is not initialized! (DatabaseObject.ValidateInput)");
230             return false;
231         }
232         //check server, dbrep or filepath
233         if (string.IsNullOrEmpty(_ServerName)) {
234             Connector.ReturnMessages.Add("DatabaseObject can not be validated : ServerName has not been provided! (DatabaseObject.ValidateInput)");
235             return false;
236         }
237 
238         if (string.IsNullOrEmpty(_FilePath)) {
239             //we must have a rep id
240             if (string.IsNullOrEmpty(_ReplicationID)) {
241                 Connector.ReturnMessages.Add("DatabaseObject can not be validated : FilePath/ReplicationID has not been provided! (DatabaseObject.ValidateInput)");
242                 return false;
243             }
244         }
245         return true;
246     }
247 
248     /// <summary>
249     /// Retrieve a document from this database by universalID
250     /// <para>Triggers a document request</para>
251     /// <para>The document will be added to the property 'Documents'</para>
252     /// </summary>
253     /// <param name="unid"></param>
254     /// <returns>DocumentObject</returns>
255     public DocumentObject GetDocument(string unid) {
256         Connector.ResetReturn();
257         DocumentObject docObj = null;
258         if (_isInitialized) {
259             docObj = new DocumentObject(this, unid);
260             if  (docObj.Initialize()) {
261                 return docObj;
262             } else {
263                 return null;
264             }
265         }
266         return docObj;
267     }
268 
269     /// <summary>
270     /// Retrieve a document from this database by universalID
271     /// This function will, in addition to retrieving the default document information, retrieve the associated domino attachment file objects
272     /// <para>Triggers a document request</para>
273     /// <para>The document will be added to the property 'Documents'</para>
274     /// </summary>
275     /// <param name="unid"></param>
276     /// <returns>DocumentObject</returns>
277     public DocumentObject GetDocumentAndFiles(string unid)
278     {
279         Connector.ResetReturn();
280         DocumentObject docObj = null;
281         if (_isInitialized)
282         {
283             docObj = new DocumentObject(this, unid);
284             if (docObj.InitializeWithFiles())
285             {
286                 return docObj;
287             }
288             else
289             {
290                 return null;
291             }
292         }
293         return docObj;
294     }
