625     /// <summary>
626     /// Retrieve fields for all documents in the property 'Documents'
627     /// <para>Provide the fields separated by ;</para>
628     /// <para>This action will update the property collection Fields inside each document object</para>
629     /// </summary>
630     /// <param name="listFields"></param>
631     /// <returns>Boolean</returns>
632     public bool LoadDocumentFields(string listFields) {
633         Connector.ResetReturn();
634         if (_isInitialized && _Documents != null && _Documents.Count > 0) {
635             if (_Session.Connection.Request.ExecuteAllFieldsRequest(_Session.WebServiceURL,this,listFields)) {
636                 return true;
637             } else {
638                 return false;
639             }
640         }
641         return true;
642     }
643 
644     /// <summary>
645     /// Retrieve fields for all documents in the property 'Documents'
646     /// <para>This action will update the property collection Fields inside each document object</para>
647     /// </summary>
648     /// <param name="listFields"></param>
649     /// <returns>Boolean</returns>
650     public bool LoadDocumentFields(IList listFields) {
651         Connector.ResetReturn();
652         if (_isInitialized && _Documents != null && _Documents.Count > 0) {
653             string fields = Common.GetListAsString(listFields, ";");
654             if (_Session.Connection.Request.ExecuteAllFieldsRequest(_Session.WebServiceURL, this, fields)) {
655                 return true;
656             } else {
657                 return false;
658             }
659         }
660         return true;
661     }
