663     /// <summary>
664     /// Retrieve file objects for all documents in the property 'Documents'
665     /// <para>This action will update the property collection Files inside each document object</para>
666     /// </summary>
667     /// <returns>Boolean</returns>
668     public bool LoadDocumentFiles()
669     {
670         Connector.ResetReturn();
671         if (_isInitialized && _Documents != null && _Documents.Count > 0)
672         {
673             if (_Session.Connection.Request.ExecuteAllFilesRequest(_Session.WebServiceURL, this))
674             {
675                 return true;
676             }
677             else
678             {
679                 return false;
680             }
681         }
682         return true;
