211     #region Private Methods
212 
213     private bool ValidateInput() {
214         if (_Database == null || !_Database.IsInitialized) {
215             //can only be initialized if valid connector and connector is initialized and connected, and session is initialized & databaseobject isInitialized
216             Connector.ReturnMessages.Add("DocumentObject can not be validated : Database is not initialized! (DocumentObject.ValidateInput)");
217             return false;
218         }
219 
220         if (string.IsNullOrEmpty(_UniversalID)) {
221             //can be empty if we are searching by key
222             if (string.IsNullOrEmpty(_SearchField) && string.IsNullOrEmpty(_SearchValue)) {
223                 //search by formula?
224                 if (string.IsNullOrEmpty(_Formula)) {
225                     Connector.ReturnMessages.Add("DocumentObject can not be validated : UniversalID, SearchField, SearchValue or Formula have not been provided! (DocumentObject.ValidateInput)");
226                     return false;
227                 }
228             }
229         }
230         return true;
231     }
232 
233     #endregion
234 
235     #region Public Methods
236 
237     /// <summary>
238     /// Initializes the document by validating the input and triggering the document request
239     /// </summary>
240     /// <returns>Boolean</returns>
241     public bool Initialize() {
242 
243         Connector.ResetReturn();
244 
245         if (!ValidateInput()) {
246             _isInitialized = false;
247             Connector.hasError = true;
248             return false;   // throws exception
249         }
250 
251         // make a connection to the webservice database - this will check the users authentication on that database
252         if (_Database.Session.Connection.Request.ExecuteDocumentRequest(_Database.Session.WebServiceURL, _UniversalID, _SearchField, _SearchValue, _Formula, _Database, this)) {
253             _isInitialized = true;
254             //  Connector.ReturnMessages.Add("Document Initialized : " + _UniversalID + " in : " + _Database.FilePath + " from : "+_Database.ServerName + " (DocumentObject.Initialize)");
255             Connector.hasError = false;
256             return true;
257         } else {
258             //error messages written to Connection.ReturnMessages by Connection.Request.ExecuteSessionRequest
259             _isInitialized = false;
260             return false;
261         }
262     }
263 
264     /// <summary>
265     /// Retrieve fields for this documents by triggering the field request
266     /// <para>Provide the fields separated by ;</para>
267     /// <para>This action will update the property 'Fields'</para>
268     /// </summary>
269     /// <param name="fields"></param>
270     /// <returns>Boolean</returns>
271     public bool GetFields(string fields) {
272         Connector.ResetReturn();
273 
274         if (_isInitialized) {
275             // if (!String.IsNullOrEmpty(fields)) {
276             //this will update _Fields list in this object
277             if (_Database.Session.Connection.Request.ExecuteFieldsRequest(_Database.Session.WebServiceURL, this, fields)) {
278                 Connector.hasError = false;
279                 return true;
280             }
281             //}
282         }
283         return false;
284         // get the fields from XPages
285 
286     }
287 
288     /// <summary>
289     /// Retrieve fields for this documents by triggering the field request
290     /// <para>This action will update the property 'Fields'</para>
291     /// </summary>
292     /// <param name="fields"></param>
293     /// <returns>Boolean</returns>
294     public bool GetFields(IList fields) {
295         Connector.ResetReturn();
296         string f = "";
297         if (_isInitialized) {
298 
299             f = Common.GetListAsString(fields, ";"); // can be empty - get's all fields
300             // if (!String.IsNullOrEmpty(fields)) {
301             //this will update _Fields list in this object
302             if (_Database.Session.Connection.Request.ExecuteFieldsRequest(_Database.Session.WebServiceURL, this, f)) {
303                 Connector.hasError = false;
304                 return true;
305             }
306             //}
307 
308         }
309         return false;
310     }
311 
312     /// <summary>
313     /// Retrieve all fields for this documents by triggering the field request
314     /// <para> This action will update the property 'Fields' </para>
315     /// </summary>
316     /// <returns>Boolean</returns>
317     public bool GetAllFields() {
318         //get all the fields from the domino document and store them in Fields
319         Connector.ResetReturn();
320 
321         if (_isInitialized) {
322 
323             if (_Database.Session.Connection.Request.ExecuteFieldsRequest(_Database.Session.WebServiceURL, this, null)) {
324                 Connector.hasError = false;
325                 return true;
326             }
327 
328         }
329         return false;
330 
331     }
332 
333     /// <summary>
334     /// Exports the content of the property 'Fields' to a file
335     /// <para>Provide the path to the file and specify if you want ot overwrite this file if it exists</para>
336     /// </summary>
337     /// <param name="filePath"></param>
338     /// <param name="overwriteExistingFile"></param>
339     /// <returns></returns>
340     public bool ExportFields(string filePath, bool overwriteExistingFile) {
341         Connector.ResetReturn();
342         try {
343             if (Fields != null && Fields.Count > 0) {
344                 //write all to stringbuilder, output to file 
345                 StringBuilder sb = new StringBuilder();
346                 foreach (KeyValuePair<string, FieldObject> field in Fields) {
347                     if (field.Value.Type.Equals("[FIELDNOTFOUND]")) {
348                         sb.AppendLine(field.Value.Name + " : " + field.Value.Type); // add type to indicate the field was not found
349                     } else {
350                         sb.AppendLine(field.Value.Name + " : " + field.Value.GetValueAsString()); //+ " (" + field.Value.Type + ")");
351                     }
352                 }
353                 if (File.Exists(filePath)) {
354                     if (overwriteExistingFile) {
355                         File.Delete(filePath);
356                     } else {
357                         Connector.ReturnMessages.Add("Unable to export the fields to the file : " + filePath);
358                         Connector.ReturnMessages.Add("A file already exist with the same name, provide a new file path");
359                         // Connector.hasError = true;
360                         return false;
361                     }
362                 }
363                 //File.CreateText(filePath);
364                 File.WriteAllText(filePath, sb.ToString());
365                 Connector.ReturnMessages.Add("Exported all fields of document : " + UniversalID + " to the file : " + filePath);
366                 return true;
367             } else {
368                 Connector.ReturnMessages.Add("The document : " + UniversalID + " does not contain any fields to export");
369                 return false;
370             }
371 
372         } catch (Exception ex) {
373             Connector.ReturnMessages.Add("Unable to export the fields to the file : " + filePath);
374             Connector.ReturnMessages.Add(Common.GetErrorInfo(ex));
375             Connector.hasError = true;
376             return false;
377         }
378     }
