7 /// <summary>
8 /// An object representing a Domino Document
9 /// </summary>
10 /// <example>The following is an example of the usage of a
11 /// <c>DocumentObject</c>:
12 ///   <code>
13 ///    DocumentObject docObj = new DocumentObject(dbObj, universalID); // create new doc object by databaseobject and universalID
14 ///    // DocumentObject docObj = new DocumentObject(dbObj, SeachField, SearchValue); // create new doc object by databaseobject and supplying a searchfield/value
15 ///    // DocumentObject docObj = new DocumentObject(formula, dbObj); // create new doc object by databaseobject and supplying a formula
16 ///    if(docObj!=null &amp;&amp; docObj.Initialize()){
17 ///         // here you can then get the fields of the document object
18 ///         // your code here...
19 ///    }
20 ///   </code>
21 /// </example>
22 public class DocumentObject {
