129     private bool ValidateInput() {
130         if (Database == null || !Database.IsInitialized) {
131             //can only be initialized if valid connector and connector is initialized and connected, and session is initialized & databaseobject isInitialized
132             Connector.ReturnMessages.Add("DocumentObject can not be validated : Database is not initialized! (DocumentObject.ValidateInput)");
133             return false;
134         }
135 
136         if (string.IsNullOrEmpty(UniversalID)) {
137             //can be empty if we are searching by key
138             if (string.IsNullOrEmpty(_SearchField) && string.IsNullOrEmpty(_SearchValue)) {
139                 //search by formula?
140                 if (string.IsNullOrEmpty(_Formula)) {
141                     Connector.ReturnMessages.Add("DocumentObject can not be validated : UniversalID, SearchField, SearchValue or Formula have not been provided! (DocumentObject.ValidateInput)");
142                     return false;
143                 }
144             }
145         }
146         return true;
147     }
148 
