226             IsInitialized = true;
227             //  Connector.ReturnMessages.Add("Document Initialized : " + _UniversalID + " in : " + _Database.FilePath + " from : "+_Database.ServerName + " (DocumentObject.Initialize)");
228             Connector.HasError = false;
229             return true;
230         } else {
231             //error messages written to Connection.ReturnMessages by Connection.Request.ExecuteSessionRequest
232             IsInitialized = false;
233             return false;
234         }
235     }
