237     /// <summary>
238     /// Initializes the document by validating the input and triggering the document request
239     /// <para>This function will, in addition to retrieving the default document information, retrieve the associated domino attachment file objects and specific fields</para>
240     /// <para>Provide the fields separated by ;</para>
241     /// </summary>
242     /// <param name="fields">Field Names separated by ; </param>
243     /// <returns>bool</returns>
244     public bool InitializeWithFilesAndFields(string fields) {
245         Connector.ResetReturn();
246 
247         if (!ValidateInput()) {
248             IsInitialized = false;
249             Connector.HasError = true;
250             return false;   // throws exception
251         }
252 
253         // make a connection to the webservice database - this will check the users authentication on that database
254         if (Database.Session.Connection.Request.ExecuteDocumentFilesFieldsRequest(Database.Session.WebServiceURL, UniversalID, _SearchField, _SearchValue, _Formula, fields, Database, this)) {
255             IsInitialized = true;
256             //  Connector.ReturnMessages.Add("Document Initialized : " + _UniversalID + " in : " + _Database.FilePath + " from : "+_Database.ServerName + " (DocumentObject.Initialize)");
257             Connector.HasError = false;
258             return true;
259         } else {
260             //error messages written to Connection.ReturnMessages by Connection.Request.ExecuteSessionRequest
261             IsInitialized = false;
262             return false;
263         }
264     }
