267     /// <summary>
268     /// Retrieve fields for this documents by triggering the field request
269     /// <para>Provide the fields separated by ;</para>
270     /// <para>This action will update the property 'Fields'</para>
271     /// </summary>
272     /// <param name="fields"></param>
273     /// <returns>bool</returns>
274     public bool GetFields(string fields) {
275         Connector.ResetReturn();
276 
277         if (IsInitialized) {
278             // if (!String.IsNullOrEmpty(fields)) {
279             //this will update _Fields list in this object
280             if (Database.Session.Connection.Request.ExecuteFieldsRequest(Database.Session.WebServiceURL, this, fields)) {
281                 Connector.HasError = false;
282                 return true;
283             }
284             //}
285         }
286         return false;
287         // get the fields from XPages
288     }
289 
290     /// <summary>
291     /// Retrieve file objects for this documents by triggering the document files request
292     /// <para>This action will update the property 'Files'</para>
293     /// </summary>
294     /// <returns>Boolean</returns>
295     public bool GetFiles() {
296         Connector.ResetReturn();
297         // get the files from XPages
298         if (IsInitialized) {
299             //this will update _Fields list in this object
300             //string WebServiceURL, string Unid, string searchField, string searchValue, string formula, DatabaseObject dbObj, DocumentObject docObj
301             if (Database.Session.Connection.Request.ExecuteDocumentFilesRequest(Database.Session.WebServiceURL, this.UniversalID, this._SearchField, this._SearchValue, this._Formula, this.Database, this)) {
302                 Connector.HasError = false;
303                 return true;
304             }
305         }
306         return false;
307     }
308 
309     /// <summary>
310     /// Retrieve fields for this documents by triggering the field request
311     /// <para>This action will update the property 'Fields'</para>
312     /// </summary>
313     /// <param name="fields"></param>
314     /// <returns>Boolean</returns>
315     public bool GetFields(IList fields) {
316         Connector.ResetReturn();
317         string f = "";
318         if (IsInitialized) {
319             f = Common.GetListAsString(fields, ";"); // can be empty - get's all fields
320             // if (!String.IsNullOrEmpty(fields)) {
321             //this will update _Fields list in this object
322             if (Database.Session.Connection.Request.ExecuteFieldsRequest(Database.Session.WebServiceURL, this, f)) {
323                 Connector.HasError = false;
324                 return true;
325             }
326             //}
327         }
328         return false;
329     }
330 
331     /// <summary>
332     /// Retrieve all fields for this documents by triggering the all fields request
333     /// <para> This action will update the property 'Fields' </para>
334     /// </summary>
335     /// <returns>Boolean</returns>
336     public bool GetAllFields() {
337         //get all the fields from the domino document and store them in Fields
338         Connector.ResetReturn();
339 
340         if (IsInitialized) {
341             if (Database.Session.Connection.Request.ExecuteFieldsRequest(Database.Session.WebServiceURL, this, null)) {
342                 Connector.HasError = false;
343                 return true;
344             }
345         }
346         return false;
347     }
