349     /// <summary>
350     /// Exports the content of the property 'Fields' to a file
351     /// <para>Provide the path to the file and specify if you want to overwrite this file if it exists</para>
352     /// </summary>
353     /// <param name="filePath"></param>
354     /// <param name="overwriteExistingFile"></param>
355     /// <returns></returns>
356     public bool ExportFields(string filePath, bool overwriteExistingFile) {
357         Connector.ResetReturn();
358         try {
359             if (Fields != null && Fields.Count > 0) {
360                 //write all to stringbuilder, output to file
361                 StringBuilder sb = new StringBuilder();
362                 foreach (KeyValuePair<string, FieldObject> field in Fields) {
363                     if (field.Value.Type.Equals("[FIELDNOTFOUND]")) {
364                         sb.AppendLine(field.Value.Name + " : " + field.Value.Type); // add type to indicate the field was not found
365                     } else {
366                         sb.AppendLine(field.Value.Name + " : " + field.Value.GetValueAsString()); //+ " (" + field.Value.Type + ")");
367                     }
368                 }
369                 if (File.Exists(filePath)) {
370                     if (overwriteExistingFile) {
371                         File.Delete(filePath);
372                     } else {
373                         Connector.ReturnMessages.Add("Unable to export the fields to the file : " + filePath);
374                         Connector.ReturnMessages.Add("A file already exist with the same name, provide a new file path");
375                         // Connector.hasError = true;
376                         return false;
377                     }
378                 }
379                 //File.CreateText(filePath);
380                 File.WriteAllText(filePath, sb.ToString());
381                 Connector.ReturnMessages.Add("Exported all fields of document : " + UniversalID + " to the file : " + filePath);
382                 return true;
383             } else {
384                 Connector.ReturnMessages.Add("The document : " + UniversalID + " does not contain any fields to export");
385                 return false;
386             }
387         } catch (Exception ex) {
388             Connector.ReturnMessages.Add("Unable to export the fields to the file : " + filePath);
389             Connector.ReturnMessages.Add(Common.GetErrorInfo(ex));
390             Connector.HasError = true;
391             return false;
392         }
393     }
394 
395     /// <summary>
396     /// Exports the files from the XPages URL for all FileObjects in the Files property
397     /// <para>Provide the path to the file and specify if you want to overwrite this file if it exists</para>
398     /// </summary>
399     /// <param name="filePath"></param>
400     /// <param name="overwriteExistingFile"></param>
401     /// <returns></returns>
402     public bool ExportFiles(string filePath, bool overwriteExistingFile) {
403         Connector.ResetReturn();
404         try {
405             if (Files != null && Files.Count > 0) {
406                 if (!Directory.Exists(filePath)) {
407                     //create folde structure
408                     Directory.CreateDirectory(filePath);
409                 }
410 
411                 foreach (FileObject fObj in Files.Values) {
412                     string fileName = "";
413                     fileName = System.IO.Path.Combine(filePath, fObj.Name);
414                     if (File.Exists(fileName)) {
415                         if (overwriteExistingFile) {
416                             File.Delete(fileName);
417                         } else {
418                             Connector.ReturnMessages.Add("Unable to export the file to : " + filePath);
419                             Connector.ReturnMessages.Add("A file already exist with the same name, provide a new file path");
420                             // Connector.hasError = true;
421                             return false;
422                         }
423                     }
424 
425                     if (!fObj.ExtractFile(fileName)) {
426                         Connector.ReturnMessages.Insert(0, "Unable to export the file : " + fObj.Name);
427                         Connector.HasError = true;
428                         return false;
429                     }
430                 }
431 
432                 Connector.ReturnMessages.Add("Exported all fields of document : " + UniversalID + " to the file : " + filePath);
433                 return true;
434             } else {
435                 Connector.ReturnMessages.Add("The document : " + UniversalID + " does not contain any fields to export");
436                 return false;
437             }
438         } catch (Exception ex) {
439             Connector.ReturnMessages.Add("Unable to export the fields to the file : " + filePath);
440             Connector.ReturnMessages.Add(Common.GetErrorInfo(ex));
441             Connector.HasError = true;
442             return false;
443         }
444     }
