228     #region Private Methods
229 
230     private bool ValidateInput()
231     {
232         if (_Database == null || !_Database.IsInitialized)
233         {
234             //can only be initialized if valid connector and connector is initialized and connected, and session is initialized & databaseobject isInitialized
235             Connector.ReturnMessages.Add("DocumentObject can not be validated : Database is not initialized! (DocumentObject.ValidateInput)");
236             return false;
237         }
238 
239         if (string.IsNullOrEmpty(_UniversalID))
240         {
241             //can be empty if we are searching by key
242             if (string.IsNullOrEmpty(_SearchField) && string.IsNullOrEmpty(_SearchValue))
243             {
244                 //search by formula?
245                 if (string.IsNullOrEmpty(_Formula))
246                 {
247                     Connector.ReturnMessages.Add("DocumentObject can not be validated : UniversalID, SearchField, SearchValue or Formula have not been provided! (DocumentObject.ValidateInput)");
248                     return false;
249                 }
250             }
251         }
252         return true;
253     }
254 
255     #endregion
