259     /// <summary>
260     /// Initializes the document by validating the input and triggering the document request
261     /// </summary>
262     /// <returns>Boolean</returns>
263     public bool Initialize()
264     {
265 
266         Connector.ResetReturn();
267 
268         if (!ValidateInput())
269         {
270             _isInitialized = false;
271             Connector.hasError = true;
272             return false;   // throws exception
273         }
274 
275         // make a connection to the webservice database - this will check the users authentication on that database
276         if (_Database.Session.Connection.Request.ExecuteDocumentRequest(_Database.Session.WebServiceURL, _UniversalID, _SearchField, _SearchValue, _Formula, _Database, this))
277         {
278             _isInitialized = true;
279             //  Connector.ReturnMessages.Add("Document Initialized : " + _UniversalID + " in : " + _Database.FilePath + " from : "+_Database.ServerName + " (DocumentObject.Initialize)");
280             Connector.hasError = false;
281             return true;
282         }
283         else
284         {
285             //error messages written to Connection.ReturnMessages by Connection.Request.ExecuteSessionRequest
286             _isInitialized = false;
287             return false;
288         }
289     }
290 
291     /// <summary>
292     /// Initializes the document by validating the input and triggering the document request
293     /// This function will, in addition to retrieving the default document information, retrieve the associated domino attachment file objects
294     /// </summary>
295     /// <returns>Boolean</returns>
296     public bool InitializeWithFiles()
297     {
298 
299         Connector.ResetReturn();
300 
301         if (!ValidateInput())
302         {
303             _isInitialized = false;
304             Connector.hasError = true;
305             return false;   // throws exception
306         }
307 
308         // make a connection to the webservice database - this will check the users authentication on that database
309         if (_Database.Session.Connection.Request.ExecuteDocumentFilesRequest(_Database.Session.WebServiceURL, _UniversalID, _SearchField, _SearchValue, _Formula, _Database, this))
310         {
311             _isInitialized = true;
312             //  Connector.ReturnMessages.Add("Document Initialized : " + _UniversalID + " in : " + _Database.FilePath + " from : "+_Database.ServerName + " (DocumentObject.Initialize)");
313             Connector.hasError = false;
314             return true;
315         }
316         else
317         {
318             //error messages written to Connection.ReturnMessages by Connection.Request.ExecuteSessionRequest
319             _isInitialized = false;
320             return false;
321         }
322     }
