324     /// <summary>
325     /// Retrieve fields for this documents by triggering the field request
326     /// <para>Provide the fields separated by ;</para>
327     /// <para>This action will update the property 'Fields'</para>
328     /// </summary>
329     /// <param name="fields"></param>
330     /// <returns>Boolean</returns>
331     public bool GetFields(string fields)
332     {
333         Connector.ResetReturn();
334 
335         if (_isInitialized)
336         {
337             // if (!String.IsNullOrEmpty(fields)) {
338             //this will update _Fields list in this object
339             if (_Database.Session.Connection.Request.ExecuteFieldsRequest(_Database.Session.WebServiceURL, this, fields))
340             {
341                 Connector.hasError = false;
342                 return true;
343             }
344             //}
345         }
346         return false;
347         // get the fields from XPages
348 
349     }
