351     /// <summary>
352     /// Retrieve file objects for this documents by triggering the document files request
353     /// <para>This action will update the property 'Files'</para>
354     /// </summary>
355     /// <returns>Boolean</returns>
356     public bool GetFiles()
357     {
358         Connector.ResetReturn();
359         // get the files from XPages
360         if (_isInitialized)
361         {
362 
363             //this will update _Fields list in this object
364             //string WebServiceURL, string Unid, string searchField, string searchValue, string formula, DatabaseObject dbObj, DocumentObject docObj
365             if (_Database.Session.Connection.Request.ExecuteDocumentFilesRequest(_Database.Session.WebServiceURL, this._UniversalID,this._SearchField,this._SearchValue, this._Formula,this._Database, this))
366             {
367                 Connector.hasError = false;
368                 return true;
369             }
370 
371         }
372         return false;
373     }
