375     /// <summary>
376     /// Retrieve fields for this documents by triggering the field request
377     /// <para>This action will update the property 'Fields'</para>
378     /// </summary>
379     /// <param name="fields"></param>
380     /// <returns>Boolean</returns>
381     public bool GetFields(IList fields)
382     {
383         Connector.ResetReturn();
384         string f = "";
385         if (_isInitialized)
386         {
387 
388             f = Common.GetListAsString(fields, ";"); // can be empty - get's all fields
389             // if (!String.IsNullOrEmpty(fields)) {
390             //this will update _Fields list in this object
391             if (_Database.Session.Connection.Request.ExecuteFieldsRequest(_Database.Session.WebServiceURL, this, f))
392             {
393                 Connector.hasError = false;
394                 return true;
395             }
396             //}
397 
398         }
399         return false;
400     }
