405     /// Retrieve all fields for this documents by triggering the all fields request
406     /// <para> This action will update the property 'Fields' </para>
407     /// </summary>
408     /// <returns>Boolean</returns>
409     public bool GetAllFields()
410     {
411         //get all the fields from the domino document and store them in Fields
412         Connector.ResetReturn();
413 
414         if (_isInitialized)
415         {
416 
417             if (_Database.Session.Connection.Request.ExecuteFieldsRequest(_Database.Session.WebServiceURL, this, null))
418             {
419                 Connector.hasError = false;
420                 return true;
421             }
422 
423         }
424         return false;
425 
426     }
