428     /// <summary>
429     /// Exports the content of the property 'Fields' to a file
430     /// <para>Provide the path to the file and specify if you want to overwrite this file if it exists</para>
431     /// </summary>
432     /// <param name="filePath"></param>
433     /// <param name="overwriteExistingFile"></param>
434     /// <returns></returns>
435     public bool ExportFields(string filePath, bool overwriteExistingFile)
436     {
437         Connector.ResetReturn();
438         try
439         {
440             if (Fields != null && Fields.Count > 0)
441             {
442                 //write all to stringbuilder, output to file 
443                 StringBuilder sb = new StringBuilder();
444                 foreach (KeyValuePair<string, FieldObject> field in Fields)
445                 {
446                     if (field.Value.Type.Equals("[FIELDNOTFOUND]"))
447                     {
448                         sb.AppendLine(field.Value.Name + " : " + field.Value.Type); // add type to indicate the field was not found
449                     }
450                     else
451                     {
452                         sb.AppendLine(field.Value.Name + " : " + field.Value.GetValueAsString()); //+ " (" + field.Value.Type + ")");
453                     }
454                 }
455                 if (File.Exists(filePath))
456                 {
457                     if (overwriteExistingFile)
458                     {
459                         File.Delete(filePath);
460                     }
461                     else
462                     {
463                         Connector.ReturnMessages.Add("Unable to export the fields to the file : " + filePath);
464                         Connector.ReturnMessages.Add("A file already exist with the same name, provide a new file path");
465                         // Connector.hasError = true;
466                         return false;
467                     }
468                 }
469                 //File.CreateText(filePath);
470                 File.WriteAllText(filePath, sb.ToString());
471                 Connector.ReturnMessages.Add("Exported all fields of document : " + UniversalID + " to the file : " + filePath);
472                 return true;
473             }
474             else
475             {
476                 Connector.ReturnMessages.Add("The document : " + UniversalID + " does not contain any fields to export");
477                 return false;
478             }
479 
480         }
481         catch (Exception ex)
482         {
483             Connector.ReturnMessages.Add("Unable to export the fields to the file : " + filePath);
484             Connector.ReturnMessages.Add(Common.GetErrorInfo(ex));
485             Connector.hasError = true;
486             return false;
487         }
488     }
489 
490     /// <summary>
491     /// Exports the files from the XPages URL for all FileObjects in the Files property
492     /// <para>Provide the path to the file and specify if you want to overwrite this file if it exists</para>
493     /// </summary>
494     /// <param name="filePath"></param>
495     /// <param name="overwriteExistingFile"></param>
496     /// <returns></returns>
497     public bool ExportFiles(string filePath, bool overwriteExistingFile)
498     {
499         Connector.ResetReturn();
500         try
501         {
502             if (Files != null && Files.Count > 0)
503             {
504               
505                 if (!Directory.Exists(filePath))
506                 {
507                     //create folde structure
508                     Directory.CreateDirectory(filePath);
509                 }
510                 
511                 foreach(FileObject fObj in Files.Values)
512                 {
513                     string fileName = "";
514                     fileName = System.IO.Path.Combine(filePath,fObj.Name);
515                     if (File.Exists(fileName))
516                     {
517                         if (overwriteExistingFile) {
518                             File.Delete(fileName);
519                         }
520                         else
521                         {
522                             Connector.ReturnMessages.Add("Unable to export the file to : " + filePath);
523                             Connector.ReturnMessages.Add("A file already exist with the same name, provide a new file path");
524                             // Connector.hasError = true;
525                             return false;
526                         }
527                       
528                     }
529 
530                     if (!fObj.ExtractFile(fileName))
531                     {
532                         Connector.ReturnMessages.Insert(0,"Unable to export the file : " + fObj.Name);
533                         Connector.hasError = true;
534                         return false;
535                     }
536                     
537                 }
538 
539              
540                 Connector.ReturnMessages.Add("Exported all fields of document : " + UniversalID + " to the file : " + filePath);
541                 return true;
542             }
543             else
544             {
545                 Connector.ReturnMessages.Add("The document : " + UniversalID + " does not contain any fields to export");
546                 return false;
547             }
548 
549         }
550         catch (Exception ex)
551         {
552             Connector.ReturnMessages.Add("Unable to export the fields to the file : " + filePath);
553             Connector.ReturnMessages.Add(Common.GetErrorInfo(ex));
554             Connector.hasError = true;
555             return false;
556         }
557     }
