108     /// <param name="SearchValue"></param>
109     public DocumentObject(DatabaseObject dbObj, string SeachField, string SearchValue) {
110         Database = dbObj;
111         _SearchField = SeachField;
112         _SearchValue = SearchValue;
113     }
114 
115     /// <summary>
116     /// DocumentObject Constructor
117     /// </summary>
118     /// <param name="formula"></param>
119     /// <param name="dbObj"></param>
120     public DocumentObject(string formula, DatabaseObject dbObj) {
121         Database = dbObj;
122         _Formula = formula;
123     }
