127     #region Private Methods
128 
129     private bool ValidateInput() {
130         if (Database == null || !Database.IsInitialized) {
131             //can only be initialized if valid connector and connector is initialized and connected, and session is initialized & databaseobject isInitialized
132             Connector.ReturnMessages.Add("DocumentObject can not be validated : Database is not initialized! (DocumentObject.ValidateInput)");
133             return false;
134         }
135 
136         if (string.IsNullOrEmpty(UniversalID)) {
137             //can be empty if we are searching by key
138             if (string.IsNullOrEmpty(_SearchField) && string.IsNullOrEmpty(_SearchValue)) {
139                 //search by formula?
140                 if (string.IsNullOrEmpty(_Formula)) {
141                     Connector.ReturnMessages.Add("DocumentObject can not be validated : UniversalID, SearchField, SearchValue or Formula have not been provided! (DocumentObject.ValidateInput)");
142                     return false;
143                 }
144             }
145         }
146         return true;
147     }
148 
149     #endregion Private Methods
150 
151     #region Public Methods
152 
153     /// <summary>
154     /// Initializes the document by validating the input and triggering the document request
155     /// </summary>
156     /// <returns>Boolean</returns>
157     public bool Initialize() {
158         Connector.ResetReturn();
159 
160         // reset the lists
161         Fields = null;
162         Files = null;
163 
164         if (!ValidateInput()) {
165             IsInitialized = false;
166             Connector.HasError = true;
167             return false;   // throws exception
168         }
169 
170         // make a connection to the webservice database - this will check the users authentication on that database
171         if (Database.Session.Connection.Request.ExecuteDocumentRequest(Database.Session.WebServiceURL, UniversalID, _SearchField, _SearchValue, _Formula, Database, this)) {
172             IsInitialized = true;
173             //  Connector.ReturnMessages.Add("Document Initialized : " + _UniversalID + " in : " + _Database.FilePath + " from : "+_Database.ServerName + " (DocumentObject.Initialize)");
174             Connector.HasError = false;
175             return true;
176         } else {
177             //error messages written to Connection.ReturnMessages by Connection.Request.ExecuteSessionRequest
178             IsInitialized = false;
179             return false;
180         }
181     }
182 
183     /// <summary>
184     /// Initializes the document by validating the input and triggering the document request
185     /// This function will, in addition to retrieving the default document information, retrieve the associated domino attachment file objects
186     /// </summary>
187     /// <returns>Boolean</returns>
188     public bool InitializeWithFiles() {
189         Connector.ResetReturn();
190 
191         if (!ValidateInput()) {
192             IsInitialized = false;
193             Connector.HasError = true;
194             return false;   // throws exception
195         }
196 
197         // make a connection to the webservice database - this will check the users authentication on that database
198         if (Database.Session.Connection.Request.ExecuteDocumentFilesRequest(Database.Session.WebServiceURL, UniversalID, _SearchField, _SearchValue, _Formula, Database, this)) {
199             IsInitialized = true;
200             //  Connector.ReturnMessages.Add("Document Initialized : " + _UniversalID + " in : " + _Database.FilePath + " from : "+_Database.ServerName + " (DocumentObject.Initialize)");
201             Connector.HasError = false;
202             return true;
203         } else {
204             //error messages written to Connection.ReturnMessages by Connection.Request.ExecuteSessionRequest
205             IsInitialized = false;
206             return false;
207         }
208     }
209 
210     /// <summary>
211     /// Retrieve fields for this documents by triggering the field request
212     /// <para>Provide the fields separated by ;</para>
213     /// <para>This action will update the property 'Fields'</para>
214     /// </summary>
215     /// <param name="fields"></param>
216     /// <returns>Boolean</returns>
217     public bool GetFields(string fields) {
218         Connector.ResetReturn();
219 
220         if (IsInitialized) {
221             // if (!String.IsNullOrEmpty(fields)) {
222             //this will update _Fields list in this object
223             if (Database.Session.Connection.Request.ExecuteFieldsRequest(Database.Session.WebServiceURL, this, fields)) {
224                 Connector.HasError = false;
225                 return true;
226             }
227             //}
228         }
229         return false;
230         // get the fields from XPages
231     }
232 
233     /// <summary>
234     /// Retrieve file objects for this documents by triggering the document files request
235     /// <para>This action will update the property 'Files'</para>
236     /// </summary>
237     /// <returns>Boolean</returns>
238     public bool GetFiles() {
239         Connector.ResetReturn();
240         // get the files from XPages
241         if (IsInitialized) {
242             //this will update _Fields list in this object
243             //string WebServiceURL, string Unid, string searchField, string searchValue, string formula, DatabaseObject dbObj, DocumentObject docObj
244             if (Database.Session.Connection.Request.ExecuteDocumentFilesRequest(Database.Session.WebServiceURL, this.UniversalID, this._SearchField, this._SearchValue, this._Formula, this.Database, this)) {
245                 Connector.HasError = false;
246                 return true;
247             }
248         }
249         return false;
250     }
251 
252     /// <summary>
253     /// Retrieve fields for this documents by triggering the field request
254     /// <para>This action will update the property 'Fields'</para>
255     /// </summary>
256     /// <param name="fields"></param>
257     /// <returns>Boolean</returns>
258     public bool GetFields(IList fields) {
259         Connector.ResetReturn();
260         string f = "";
261         if (IsInitialized) {
262             f = Common.GetListAsString(fields, ";"); // can be empty - get's all fields
263             // if (!String.IsNullOrEmpty(fields)) {
264             //this will update _Fields list in this object
265             if (Database.Session.Connection.Request.ExecuteFieldsRequest(Database.Session.WebServiceURL, this, f)) {
266                 Connector.HasError = false;
267                 return true;
268             }
269             //}
270         }
271         return false;
272     }
273 
274     /// <summary>
275     /// Retrieve all fields for this documents by triggering the all fields request
276     /// <para> This action will update the property 'Fields' </para>
277     /// </summary>
278     /// <returns>Boolean</returns>
279     public bool GetAllFields() {
280         //get all the fields from the domino document and store them in Fields
281         Connector.ResetReturn();
282 
283         if (IsInitialized) {
284             if (Database.Session.Connection.Request.ExecuteFieldsRequest(Database.Session.WebServiceURL, this, null)) {
285                 Connector.HasError = false;
286                 return true;
287             }
288         }
289         return false;
290     }
291 
292     /// <summary>
293     /// Exports the content of the property 'Fields' to a file
294     /// <para>Provide the path to the file and specify if you want to overwrite this file if it exists</para>
295     /// </summary>
296     /// <param name="filePath"></param>
297     /// <param name="overwriteExistingFile"></param>
298     /// <returns></returns>
299     public bool ExportFields(string filePath, bool overwriteExistingFile) {
300         Connector.ResetReturn();
301         try {
302             if (Fields != null && Fields.Count > 0) {
303                 //write all to stringbuilder, output to file
304                 StringBuilder sb = new StringBuilder();
305                 foreach (KeyValuePair<string, FieldObject> field in Fields) {
306                     if (field.Value.Type.Equals("[FIELDNOTFOUND]")) {
307                         sb.AppendLine(field.Value.Name + " : " + field.Value.Type); // add type to indicate the field was not found
308                     } else {
309                         sb.AppendLine(field.Value.Name + " : " + field.Value.GetValueAsString()); //+ " (" + field.Value.Type + ")");
310                     }
311                 }
312                 if (File.Exists(filePath)) {
313                     if (overwriteExistingFile) {
314                         File.Delete(filePath);
315                     } else {
316                         Connector.ReturnMessages.Add("Unable to export the fields to the file : " + filePath);
317                         Connector.ReturnMessages.Add("A file already exist with the same name, provide a new file path");
318                         // Connector.hasError = true;
319                         return false;
320                     }
321                 }
322                 //File.CreateText(filePath);
323                 File.WriteAllText(filePath, sb.ToString());
324                 Connector.ReturnMessages.Add("Exported all fields of document : " + UniversalID + " to the file : " + filePath);
325                 return true;
326             } else {
327                 Connector.ReturnMessages.Add("The document : " + UniversalID + " does not contain any fields to export");
328                 return false;
329             }
330         } catch (Exception ex) {
331             Connector.ReturnMessages.Add("Unable to export the fields to the file : " + filePath);
332             Connector.ReturnMessages.Add(Common.GetErrorInfo(ex));
333             Connector.HasError = true;
334             return false;
335         }
336     }
337 
338     /// <summary>
339     /// Exports the files from the XPages URL for all FileObjects in the Files property
340     /// <para>Provide the path to the file and specify if you want to overwrite this file if it exists</para>
341     /// </summary>
342     /// <param name="filePath"></param>
343     /// <param name="overwriteExistingFile"></param>
344     /// <returns></returns>
345     public bool ExportFiles(string filePath, bool overwriteExistingFile) {
346         Connector.ResetReturn();
347         try {
348             if (Files != null && Files.Count > 0) {
349                 if (!Directory.Exists(filePath)) {
350                     //create folde structure
351                     Directory.CreateDirectory(filePath);
352                 }
353 
354                 foreach (FileObject fObj in Files.Values) {
355                     string fileName = "";
356                     fileName = System.IO.Path.Combine(filePath, fObj.Name);
357                     if (File.Exists(fileName)) {
358                         if (overwriteExistingFile) {
359                             File.Delete(fileName);
360                         } else {
361                             Connector.ReturnMessages.Add("Unable to export the file to : " + filePath);
362                             Connector.ReturnMessages.Add("A file already exist with the same name, provide a new file path");
363                             // Connector.hasError = true;
364                             return false;
365                         }
366                     }
367 
368                     if (!fObj.ExtractFile(fileName)) {
369                         Connector.ReturnMessages.Insert(0, "Unable to export the file : " + fObj.Name);
370                         Connector.HasError = true;
371                         return false;
372                     }
373                 }
374 
375                 Connector.ReturnMessages.Add("Exported all fields of document : " + UniversalID + " to the file : " + filePath);
376                 return true;
377             } else {
378                 Connector.ReturnMessages.Add("The document : " + UniversalID + " does not contain any fields to export");
379                 return false;
380             }
381         } catch (Exception ex) {
382             Connector.ReturnMessages.Add("Unable to export the fields to the file : " + filePath);
383             Connector.ReturnMessages.Add(Common.GetErrorInfo(ex));
384             Connector.HasError = true;
385             return false;
386         }
387     }
