133     #region Private Methods
134 
135     private bool ValidateInput()
136     {
137         if (Database == null || !Database.IsInitialized)
138         {
139             //can only be initialized if valid connector and connector is initialized and connected, and session is initialized & databaseobject isInitialized
140             Connector.ReturnMessages.Add("DocumentObject can not be validated : Database is not initialized! (DocumentObject.ValidateInput)");
141             return false;
142         }
143 
144         if (string.IsNullOrEmpty(UniversalID))
145         {
146             //can be empty if we are searching by key
147             if (string.IsNullOrEmpty(_SearchField) && string.IsNullOrEmpty(_SearchValue))
148             {
149                 //search by formula?
150                 if (string.IsNullOrEmpty(_Formula))
151                 {
152                     Connector.ReturnMessages.Add("DocumentObject can not be validated : UniversalID, SearchField, SearchValue or Formula have not been provided! (DocumentObject.ValidateInput)");
153                     return false;
154                 }
155             }
156         }
157         return true;
158     }
159 
160     #endregion
