162     #region Public Methods
163 
164     /// <summary>
165     /// Initializes the document by validating the input and triggering the document request
166     /// </summary>
167     /// <returns>Boolean</returns>
168     public bool Initialize()
169     {
170 
171         Connector.ResetReturn();
172 
173         // reset the lists
174         Fields = null;
175         Files = null;
176 
177         if (!ValidateInput())
178         {
179             IsInitialized = false;
180             Connector.hasError = true;
181             return false;   // throws exception
182         }
183 
184         // make a connection to the webservice database - this will check the users authentication on that database
185         if (Database.Session.Connection.Request.ExecuteDocumentRequest(Database.Session.WebServiceURL, UniversalID, _SearchField, _SearchValue, _Formula, Database, this))
186         {
187             IsInitialized = true;
188             //  Connector.ReturnMessages.Add("Document Initialized : " + _UniversalID + " in : " + _Database.FilePath + " from : "+_Database.ServerName + " (DocumentObject.Initialize)");
189             Connector.hasError = false;
190             return true;
191         }
192         else
193         {
194             //error messages written to Connection.ReturnMessages by Connection.Request.ExecuteSessionRequest
195             IsInitialized = false;
196             return false;
197         }
198     }
199 
200     /// <summary>
201     /// Initializes the document by validating the input and triggering the document request
202     /// This function will, in addition to retrieving the default document information, retrieve the associated domino attachment file objects
203     /// </summary>
204     /// <returns>Boolean</returns>
205     public bool InitializeWithFiles()
206     {
207 
208         Connector.ResetReturn();
209 
210         if (!ValidateInput())
211         {
212             IsInitialized = false;
213             Connector.hasError = true;
214             return false;   // throws exception
215         }
216 
217         // make a connection to the webservice database - this will check the users authentication on that database
218         if (Database.Session.Connection.Request.ExecuteDocumentFilesRequest(Database.Session.WebServiceURL, UniversalID, _SearchField, _SearchValue, _Formula, Database, this))
219         {
220             IsInitialized = true;
221             //  Connector.ReturnMessages.Add("Document Initialized : " + _UniversalID + " in : " + _Database.FilePath + " from : "+_Database.ServerName + " (DocumentObject.Initialize)");
222             Connector.hasError = false;
223             return true;
224         }
225         else
226         {
227             //error messages written to Connection.ReturnMessages by Connection.Request.ExecuteSessionRequest
228             IsInitialized = false;
229             return false;
230         }
231     }
