233     /// <summary>
234     /// Retrieve fields for this documents by triggering the field request
235     /// <para>Provide the fields separated by ;</para>
236     /// <para>This action will update the property 'Fields'</para>
237     /// </summary>
238     /// <param name="fields"></param>
239     /// <returns>Boolean</returns>
240     public bool GetFields(string fields)
241     {
242         Connector.ResetReturn();
243 
244         if (IsInitialized)
245         {
246             // if (!String.IsNullOrEmpty(fields)) {
247             //this will update _Fields list in this object
248             if (Database.Session.Connection.Request.ExecuteFieldsRequest(Database.Session.WebServiceURL, this, fields))
249             {
250                 Connector.hasError = false;
251                 return true;
252             }
253             //}
254         }
255         return false;
256         // get the fields from XPages
257 
258     }
