260     /// <summary>
261     /// Retrieve file objects for this documents by triggering the document files request
262     /// <para>This action will update the property 'Files'</para>
263     /// </summary>
264     /// <returns>Boolean</returns>
265     public bool GetFiles()
266     {
267         Connector.ResetReturn();
268         // get the files from XPages
269         if (IsInitialized)
270         {
271 
272             //this will update _Fields list in this object
273             //string WebServiceURL, string Unid, string searchField, string searchValue, string formula, DatabaseObject dbObj, DocumentObject docObj
274             if (Database.Session.Connection.Request.ExecuteDocumentFilesRequest(Database.Session.WebServiceURL, this.UniversalID,this._SearchField,this._SearchValue, this._Formula,this.Database, this))
275             {
276                 Connector.hasError = false;
277                 return true;
278             }
279 
280         }
281         return false;
282     }
