284     /// <summary>
285     /// Retrieve fields for this documents by triggering the field request
286     /// <para>This action will update the property 'Fields'</para>
287     /// </summary>
288     /// <param name="fields"></param>
289     /// <returns>Boolean</returns>
290     public bool GetFields(IList fields)
291     {
292         Connector.ResetReturn();
293         string f = "";
294         if (IsInitialized)
295         {
296 
297             f = Common.GetListAsString(fields, ";"); // can be empty - get's all fields
298             // if (!String.IsNullOrEmpty(fields)) {
299             //this will update _Fields list in this object
300             if (Database.Session.Connection.Request.ExecuteFieldsRequest(Database.Session.WebServiceURL, this, f))
301             {
302                 Connector.hasError = false;
303                 return true;
304             }
305             //}
306 
307         }
308         return false;
309     }
