311     /// <summary>
312     /// Retrieve all fields for this documents by triggering the all fields request
313     /// <para> This action will update the property 'Fields' </para>
314     /// </summary>
315     /// <returns>Boolean</returns>
316     public bool GetAllFields()
317     {
318         //get all the fields from the domino document and store them in Fields
319         Connector.ResetReturn();
320 
321         if (IsInitialized)
322         {
323 
324             if (Database.Session.Connection.Request.ExecuteFieldsRequest(Database.Session.WebServiceURL, this, null))
325             {
326                 Connector.hasError = false;
327                 return true;
328             }
329 
330         }
331         return false;
332 
333     }
