335     /// <summary>
336     /// Exports the content of the property 'Fields' to a file
337     /// <para>Provide the path to the file and specify if you want to overwrite this file if it exists</para>
338     /// </summary>
339     /// <param name="filePath"></param>
340     /// <param name="overwriteExistingFile"></param>
341     /// <returns></returns>
342     public bool ExportFields(string filePath, bool overwriteExistingFile)
343     {
344         Connector.ResetReturn();
345         try
346         {
347             if (Fields != null && Fields.Count > 0)
348             {
349                 //write all to stringbuilder, output to file 
350                 StringBuilder sb = new StringBuilder();
351                 foreach (KeyValuePair<string, FieldObject> field in Fields)
352                 {
353                     if (field.Value.Type.Equals("[FIELDNOTFOUND]"))
354                     {
355                         sb.AppendLine(field.Value.Name + " : " + field.Value.Type); // add type to indicate the field was not found
356                     }
357                     else
358                     {
359                         sb.AppendLine(field.Value.Name + " : " + field.Value.GetValueAsString()); //+ " (" + field.Value.Type + ")");
360                     }
361                 }
362                 if (File.Exists(filePath))
363                 {
364                     if (overwriteExistingFile)
365                     {
366                         File.Delete(filePath);
367                     }
368                     else
369                     {
370                         Connector.ReturnMessages.Add("Unable to export the fields to the file : " + filePath);
371                         Connector.ReturnMessages.Add("A file already exist with the same name, provide a new file path");
372                         // Connector.hasError = true;
373                         return false;
374                     }
375                 }
376                 //File.CreateText(filePath);
377                 File.WriteAllText(filePath, sb.ToString());
378                 Connector.ReturnMessages.Add("Exported all fields of document : " + UniversalID + " to the file : " + filePath);
379                 return true;
380             }
381             else
382             {
383                 Connector.ReturnMessages.Add("The document : " + UniversalID + " does not contain any fields to export");
384                 return false;
385             }
386 
387         }
388         catch (Exception ex)
389         {
390             Connector.ReturnMessages.Add("Unable to export the fields to the file : " + filePath);
391             Connector.ReturnMessages.Add(Common.GetErrorInfo(ex));
392             Connector.hasError = true;
393             return false;
394         }
395     }
396 
397     /// <summary>
398     /// Exports the files from the XPages URL for all FileObjects in the Files property
399     /// <para>Provide the path to the file and specify if you want to overwrite this file if it exists</para>
400     /// </summary>
401     /// <param name="filePath"></param>
402     /// <param name="overwriteExistingFile"></param>
403     /// <returns></returns>
404     public bool ExportFiles(string filePath, bool overwriteExistingFile)
405     {
406         Connector.ResetReturn();
407         try
408         {
409             if (Files != null && Files.Count > 0)
410             {
411               
412                 if (!Directory.Exists(filePath))
413                 {
414                     //create folde structure
415                     Directory.CreateDirectory(filePath);
416                 }
417                 
418                 foreach(FileObject fObj in Files.Values)
419                 {
420                     string fileName = "";
421                     fileName = System.IO.Path.Combine(filePath,fObj.Name);
422                     if (File.Exists(fileName))
423                     {
424                         if (overwriteExistingFile) {
425                             File.Delete(fileName);
426                         }
427                         else
428                         {
429                             Connector.ReturnMessages.Add("Unable to export the file to : " + filePath);
430                             Connector.ReturnMessages.Add("A file already exist with the same name, provide a new file path");
431                             // Connector.hasError = true;
432                             return false;
433                         }
434                       
435                     }
436 
437                     if (!fObj.ExtractFile(fileName))
438                     {
439                         Connector.ReturnMessages.Insert(0,"Unable to export the file : " + fObj.Name);
440                         Connector.hasError = true;
441                         return false;
442                     }
443                     
444                 }
445 
446              
447                 Connector.ReturnMessages.Add("Exported all fields of document : " + UniversalID + " to the file : " + filePath);
448                 return true;
449             }
450             else
451             {
452                 Connector.ReturnMessages.Add("The document : " + UniversalID + " does not contain any fields to export");
453                 return false;
454             }
455 
456         }
457         catch (Exception ex)
458         {
459             Connector.ReturnMessages.Add("Unable to export the fields to the file : " + filePath);
460             Connector.ReturnMessages.Add(Common.GetErrorInfo(ex));
461             Connector.hasError = true;
462             return false;
463         }
464     }
