9 internal class Requestor {
10 
11     #region Variables
12 
13     private Connector Connection;
14     protected internal bool isInitialized = false;
15     private CookieContainer AuthenticationCookie;
16 
17     #endregion
18 
19     #region Constructor
20 
21     public Requestor(Connector Connector) {
22         Connection = Connector;
23     }
24     #endregion
25 
26     #region Private Methods
27 
28     private bool AddIdentityHeader(ref HttpWebRequest request) {
29         try {
30             if (request != null) {
31                 string XPIdentity = "JPI$XP@ges!C0nn3nt0r|Id3nTity@Request";
32                 if (!string.IsNullOrEmpty(Connection.XPIdentity)) {
33                     XPIdentity = Connection.XPIdentity;
34                 }
35                 //  Const XPIdentity As String = "JPI$XP@ges!C0nn3nt0r|Id3nTity@Request"
36 
37                 Encryptor EncodedEncryptedContent = new Encryptor(XPIdentity, true, ref Connection);
38                 if (EncodedEncryptedContent.Initialize()) {
39                     request.Headers.Add("Identity", EncodedEncryptedContent.EncodedContent);
40                     return true;
41                 }
42             } else {
43                 //request is nothing ' error added in calling function
44             }
45             return false;
46         } catch (Exception ex) {
47             Connector.ReturnMessages.Add("Unable to add Identity Header to XPages Request");
48             Connector.ReturnMessages.Add(Common.GetErrorInfo(ex));
49             Connector.hasError = true; //throws exception
50 
51             return false;
52         }
53     }
54 
55     #endregion
56 
57     #region Protected Internal Methods
58 
59     protected internal bool Initialize() {
60 
61         HttpWebRequest request = null;
62 
63         // use the XPagesAPI ReturnMessages & hasError
64         try {
65             //ServicePointManager.Expect100Continue = false;
66             ServicePointManager.Expect100Continue = true;
67             //  ServicePointManager.SecurityProtocol = SecurityProtocolType.Tls;
68             ServicePointManager.SecurityProtocol = SecurityProtocolType.Tls12 | SecurityProtocolType.Tls11 | SecurityProtocolType.Tls;
69             ServicePointManager.DefaultConnectionLimit = 9999;
70            
71 
72             // Cookie container to store authentication cookie
73             CookieContainer cookies = new CookieContainer();
74 
75             //create an HTTP request
76             //Login - check for "/names.nsf?Login"
77             string serverURL = "";
78             serverURL = Connection.ServerURL;
79             if (!serverURL.Contains("/names.nsf?Login")) {
80                 serverURL = serverURL + "/names.nsf?Login";
81             }
82 
83             request = (HttpWebRequest)WebRequest.Create(serverURL);
84 
85             // Prepare HTTP request
86             request.Method = WebRequestMethods.Http.Post;
87             // "POST"
88             request.AllowAutoRedirect = false;
89             request.ContentType = "application/x-www-form-urlencoded";
90             request.CookieContainer = cookies;
91             //  request.UserAgent = "Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/54.0.2840.99 Safari/537.36"
92             // Prepare POST body
93             string post = "Username=" + Uri.EscapeDataString(Connection.UserName) + "&Password=" + Uri.EscapeDataString(Connection.Password);
94             byte[] bytes = Encoding.ASCII.GetBytes(post);
95 
96             // Write data to request
97             request.ContentLength = bytes.Length;
98             Stream streamOut = request.GetRequestStream();
99             streamOut.Write(bytes, 0, bytes.Length);
100             streamOut.Close();
101 
102             //' Get response
103             HttpWebResponse response = (HttpWebResponse)request.GetResponse();
104 
105             // Check if we are authenticated properly
106             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
107                 foreach (Cookie cooky in response.Cookies) {
108                     cookies.Add(cooky);
109                 }
110 
111                 this.AuthenticationCookie = cookies;
112 
113                 if (response.Cookies != null && response.Cookies.Count > 0) {
114                     Connector.ReturnMessages.Add(Connection.UserName + " Successfully Authenticated On Domino Server " + Connection.ServerURL);
115                     Connector.hasError = false;
116                 } else {
117                     Connector.ReturnMessages.Add(Connection.UserName + " was unable to authenticate to the domino server " + Connection.ServerURL);
118                     Connector.hasError = true;
119                     //thows exception
120                 }
121             } else {
122                 Connector.ReturnMessages.Add(Connection.UserName + " was unable to authenticate to the domino server " + Connection.ServerURL);
123                 Connector.hasError = true;
124                 //thows exception
125             }
126             isInitialized = true;
127             return true;
128         } catch (Exception ex) {
129             Connector.ReturnMessages.Add(Connection.UserName + " was unable to authenticate to the domino server " + Connection.ServerURL);
130             Connector.ReturnMessages.Add(Common.GetErrorInfo(ex));
131             Connector.hasError = true;
132             //throws exception
133             isInitialized = false;
134             return false;
135         }
136 
137     }
138 
139     protected internal bool ExecuteSessionRequest(string WebServiceURL) {
140         HttpWebRequest request = null;
141 
142         try {
143             if (!isInitialized) {
144                 Connector.ReturnMessages.Add("Unable to execute the Session Request - Not Initialized/Connected To Server");
145                 Connector.hasError = true;
146                 return false;
147             }
148 
149             //ServicePointManager.Expect100Continue = false;
150 
151             //create an HTTP request
152             // WebURL use this without ?CheckOut or ?CheckIn
153 
154             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Session");
155             // ?CheckOut
156 
157             request.CookieContainer = this.AuthenticationCookie;
158             //  request.KeepAlive = True
159             request.AllowAutoRedirect = true;
160 
161             request.Method = WebRequestMethods.Http.Post;           //"POST"
162             request.ContentType = "application/jpi; charset=utf-8";         //application/json
163                                                                             // Set the request stream
164             string result;
165 
166             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
167             if (!AddIdentityHeader(ref request)) {
168                 Connector.ReturnMessages.Add("Unable to Execute Session Request : Identity Header could not be added!");
169                 Connector.hasError = true;
170                 return false;
171             }
172 
173             //test with encryption
174             StringBuilder sb = new StringBuilder();
175 
176             // User Name
177             sb.AppendLine("UserName : " + Connection.UserName);
178             sb.AppendLine("Connecting via XPagesAPI");
179 
180 
181             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
182             string myString = Encoding.UTF8.GetString(b);
183             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
184             if (EncodedEncryptedContent.Initialize()) {
185 
186                 Stream requestStream = null;
187                 try {
188                     requestStream = request.GetRequestStream();
189                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8)) {
190                         writer.Write(EncodedEncryptedContent.EncodedContent);
191                     }
192                 } catch (Exception e) {
193                     Connector.ReturnMessages.Add("Unable to execute the Session Request : " + Common.GetErrorInfo(e));
194                     Connector.hasError = true;  //throws exception
195                     return false;
196                 } finally {
197                     //if (requestStream != null) {
198                     //    requestStream.Dispose();
199                     //}
200                 }
201 
202             } else {
203                 Connector.ReturnMessages.Add("Executing Session Request - Unable to encode/encrypt the content of the request!");
204                 Connector.hasError = true; //throws exception
205                 return false;
206             }
207 
208             HttpWebResponse response = (HttpWebResponse)request.GetResponse();
209             //can give webexception error 500
210 
211             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
212                 Stream responseStream = null;
213                 try {
214                     responseStream = request.GetResponse().GetResponseStream();
215                     using (StreamReader reader = new StreamReader(responseStream, true)) {
216                         result = reader.ReadToEnd();
217                     }
218                 } catch (Exception e) {
219                     Connector.ReturnMessages.Add("Unable to execute the Session Request : " + Common.GetErrorInfo(e));
220                     Connector.hasError = true;  //throws exception
221                     return false;
222                 } finally {
223                     //if (responseStream != null) {
224                     //    responseStream.Dispose();
225                     //}
226                 }
227 
228                 if (result != null) {
229                     //check if the page returned is not just the login page - we need to get encoded & encrypted content here - check if start with <! 
230                     if (result.StartsWith("<!")) {
231                         //possible wrong password !
232                         Connector.ReturnMessages.Add("Executing Session Request -  Unable to get the session - Authentication/Password Issue");
233                         Connector.hasError = true;
234                         //throws exception
235                         return false;
236                     }
237 
238                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
239                     if (DecodeDecryptedContent.Initialize()) {
240                         ArrayList arList = new ArrayList();
241 
242                         string[] ar = null;
243                         bool isError = false;
244 
245                         string message = "";
246                         string details = "";
247 
248                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
249                         arList.AddRange(ar);
250                         foreach (string str in arList) {
251                             //YES/NO
252                             if (str.StartsWith("Error: ")) {
253                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase)) {
254                                     isError = true;
255                                 }
256                             }
257                             if (str.StartsWith("Message: ")) {
258                                 message = str.Replace("Message: ", "");
259                             }
260                             if (str.StartsWith("Details: ")) {
261                                 details = str.Replace("Details: ", "");
262                             }
263 
264                         }
265                         if (isError) {
266                             Connector.ReturnMessages.Add(message);
267                             Connector.ReturnMessages.Add(details);
268                             Connector.hasError = true;
269                             //throws exception
270                             return false;
271                         } else {
272                             Connector.ReturnMessages.Add(message);
273                             Connector.ReturnMessages.Add(details);
274                             Connector.hasError = false;
275                             //SUCCESS
276                         }
277 
278                     } else {
279                         Connector.ReturnMessages.Add("Executing Session Request - Unable to decode/decrypt the content of the response!");
280                         Connector.hasError = true;
281                         //throws exception
282                         return false;
283                     }
284                 } else {
285                     //no response from server
286                     Connector.ReturnMessages.Add("Executing Session Request -  Unable to get response from XPages!");
287                     Connector.hasError = true;
288                     //throws exception
289                     return false;
290 
291                 }
292                 return true;
293             } else {
294                 Connector.ReturnMessages.Add("Executing Session Request -  Unable to get the session - Authentication Issue");
295                 Connector.hasError = true;
296                 //throws exception
297                 return false;
298             }
299 
300         } catch (Exception ex) {
301             if (ex.GetType() == typeof(WebException)) {
302                 WebException webex;
303                 webex = (WebException)ex;
304                 if (webex.Response != null) {
305                     Stream stream = webex.Response.GetResponseStream();
306                     using (StreamReader reader = new StreamReader(stream)) {
307                         string errorResp = reader.ReadToEnd();
308                         // errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\r\\n\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\r\\n", Environment.NewLine);
309                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
310                         Connector.ReturnMessages.Add("Unable to Execute the Session Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
311                         Connector.hasError = true;
312                         //throws exception
313                         return false;
314                     }
315 
316                 } else {
317                     Connector.ReturnMessages.Add("Unable to Execute the Session Request : " + Common.GetErrorInfo(ex));
318                     Connector.hasError = true;
319                     //throws exception
320                     return false;
321                 }
322             } else {
323                 Connector.ReturnMessages.Add("Unable to Execute the Session Request : " + Common.GetErrorInfo(ex));
324                 Connector.hasError = true;
325                 //throws exception
326                 return false;
327             }
328         }
329     }
330 
331     protected internal bool ExecuteDatabaseRequest(string WebServiceURL, string ServerName, string dbFilePath, string dbReplicationId, DatabaseObject dbObj) {
332         HttpWebRequest request = null;
333 
334         try {
335             if (!isInitialized) {
336                 Connector.ReturnMessages.Add("Unable to execute the Database Request - Not Initialized/Connected To Server");
337                 Connector.hasError = true;
338                 return false;
339             }
340 
341             //  ServicePointManager.Expect100Continue = false;
342 
343             //create an HTTP request
344 
345             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Database");
346 
347             request.CookieContainer = this.AuthenticationCookie;
348             //  request.KeepAlive = True
349             request.AllowAutoRedirect = true;
350 
351             request.Method = WebRequestMethods.Http.Post;   //"POST"
352             request.ContentType = "application/jpi; charset=utf-8"; //application/json
353                                                                     // Set the request stream
354             string result;
355 
356             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
357             if (!AddIdentityHeader(ref request)) {
358                 Connector.ReturnMessages.Add("Unable to Execute Database Request : Identity Header could not be added!");
359                 Connector.hasError = true;  //throws error
360                 return false;
361             }
362 
363             //test with encryption
364             StringBuilder sb = new StringBuilder();
365 
366             sb.AppendLine("UserName: " + Connection.UserName);
367             sb.AppendLine("ServerName: " + ServerName);
368             sb.AppendLine("FilePath: " + dbFilePath);
369             sb.AppendLine("ReplicationID: " + dbReplicationId);
370 
371             sb.AppendLine("Connecting via XPagesAPI");
372 
373             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
374             string myString = Encoding.UTF8.GetString(b);
375             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
376             if (EncodedEncryptedContent.Initialize()) {
377                 Stream requestStream = null;
378                 try {
379                     requestStream = request.GetRequestStream();
380                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8)) {
381                         writer.Write(EncodedEncryptedContent.EncodedContent);
382                     }
383                 } catch (Exception e) {
384                     Connector.ReturnMessages.Add("Unable to execute the Database Request : " + Common.GetErrorInfo(e));
385                     Connector.hasError = true;  //throws exception
386                     return false;
387                 } finally {
388                     //if (requestStream != null) {
389                     //    requestStream.Dispose();
390                     //}
391                 }
392             } else {
393                 Connector.ReturnMessages.Add("Executing Database Request - Unable to encode/encrypt the content of the request!");
394                 Connector.hasError = true; //throws exception
395                 return false;
396             }
397 
398             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
399 
400             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
401                 Stream responseStream = null;
402                 try {
403                     responseStream = request.GetResponse().GetResponseStream();
404                     using (StreamReader reader = new StreamReader(responseStream, true)) {
405                         result = reader.ReadToEnd();
406                     }
407                 } catch (Exception e) {
408                     Connector.ReturnMessages.Add("Unable to execute the Database Request : " + Common.GetErrorInfo(e));
409                     Connector.hasError = true;  //throws exception
410                     return false;
411                 } finally {
412                     //if (responseStream != null) {
413                     //    responseStream.Dispose();
414                     //}
415                 }
416 
417                 if (result != null) {
418                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
419                     if (DecodeDecryptedContent.Initialize()) {
420                         ArrayList arList = new ArrayList();
421 
422                         string[] ar = null;
423                         bool isError = false;
424 
425                         string message = "";
426                         string details = "";
427                         string value = "";
428 
429                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
430                         arList.AddRange(ar);
431                         foreach (string str in arList) {
432                             //YES/NO
433                             if (str.StartsWith("Error: ")) {
434                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase)) {
435                                     isError = true;
436                                 }
437                             }
438                             if (str.StartsWith("Message: ")) {
439                                 message = str.Replace("Message: ", "");
440                             }
441                             if (str.StartsWith("Details: ")) {
442                                 details = str.Replace("Details: ", "");
443                             }
444                             if (str.StartsWith("ServerName: ")) {
445                                 value = str.Replace("ServerName: ", "");
446                                 if (!string.IsNullOrEmpty(value)) {
447                                     dbObj.ServerName = value;
448                                 }
449                             }
450                             if (str.StartsWith("FilePath: ")) {
451                                 value = str.Replace("FilePath: ", "");
452                                 if (!string.IsNullOrEmpty(value)) {
453                                     dbObj.FilePath = value;
454                                 }
455                             }
456                             if (str.StartsWith("ReplicationID: ")) {
457                                 value = str.Replace("ReplicationID: ", "");
458                                 if (!string.IsNullOrEmpty(value)) {
459                                     dbObj.ReplicationID = value;
460                                 }
461                             }
462 
463                             if (str.StartsWith("Title: ")) {
464                                 value = str.Replace("Title: ", "");
465                                 if (!string.IsNullOrEmpty(value)) {
466                                     dbObj.Title = value;
467                                 }
468                             }
469 
470                             if (str.StartsWith("Template: ")) {
471                                 value = str.Replace("Template: ", "");
472                                 if (!string.IsNullOrEmpty(value)) {
473                                     dbObj.Template = value;
474                                 }
475                             }
476 
477                             if (str.StartsWith("Size: ")) {
478                                 value = str.Replace("Size: ", "");
479                                 if (!string.IsNullOrEmpty(value)) {
480                                     dbObj.Size = value;
481                                 }
482                             }
483 
484                             if (str.StartsWith("URL: ")) {
485                                 value = str.Replace("URL: ", "");
486                                 if (!string.IsNullOrEmpty(value)) {
487                                     dbObj.Url = value;
488                                 }
489                             }
490 
491                         }
492 
493                         if (isError) {
494                             Connector.ReturnMessages.Add(message);
495                             Connector.ReturnMessages.Add(details);
496                             Connector.hasError = true; //throws exception
497                             return false;
498                         } else {
499                             Connector.ReturnMessages.Add(message);
500                             Connector.ReturnMessages.Add(details);
501                             Connector.hasError = false; //SUCCESS
502                         }
503 
504                     } else {
505                         Connector.ReturnMessages.Add("Executing Database Request - Unable to decode/decrypt the content of the response!");
506                         Connector.hasError = true;  //throws exception
507                         return false;
508                     }
509                 } else {
510                     //no response from server
511                     Connector.ReturnMessages.Add("Executing Database Request -  Unable to get response from XPages!");
512                     Connector.hasError = true;  //throws exception
513                     return false;
514 
515                 }
516                 return true;
517             } else {
518                 Connector.ReturnMessages.Add("Executing Database Request -  Unable to get the Database - Authentication Issue");
519                 Connector.hasError = true;  //throws exception
520                 return false;
521             }
522 
523         } catch (Exception ex) {
524             if (ex.GetType() == typeof(WebException)) {
525                 WebException webex;
526                 webex = (WebException)ex;
527                 if (webex.Response != null) {
528                     Stream stream = webex.Response.GetResponseStream();
529                     using (StreamReader reader = new StreamReader(stream)) {
530                         string errorResp = reader.ReadToEnd();
531                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
532                         Connector.ReturnMessages.Add("Unable to Execute the Database Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
533                         Connector.hasError = true;  //throws exception
534                         return false;
535                     }
536 
537                 } else {
538                     Connector.ReturnMessages.Add("Unable to Execute the Database Request : " + Common.GetErrorInfo(ex));
539                     Connector.hasError = true;  //throws exception
540                     return false;
541                 }
542             } else {
543                 Connector.ReturnMessages.Add("Unable to Execute the Database Request : " + Common.GetErrorInfo(ex));
544                 Connector.hasError = true;  //throws exception
545                 return false;
546             }
547         }
548     }
