551     protected internal bool ExecuteAllDatabasesRequest(string WebServiceURL, string ServerName, SessionObject sObj) {
552         HttpWebRequest request = null;
553 
554         try {
555             if (!isInitialized) {
556                 Connector.ReturnMessages.Add("Unable to execute the Database Request - Not Initialized/Connected To Server");
557                 Connector.hasError = true;
558                 return false;
559             }
560 
561             //  ServicePointManager.Expect100Continue = false;
562 
563             //create an HTTP request
564 
565             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_AllDatabases");
566 
567             request.CookieContainer = this.AuthenticationCookie;
568             //  request.KeepAlive = True
569             request.AllowAutoRedirect = true;
570 
571             request.Method = WebRequestMethods.Http.Post;   //"POST"
572             request.ContentType = "application/jpi; charset=utf-8"; //application/json
573                                                                     // Set the request stream
574             string result;
575 
576             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
577             if (!AddIdentityHeader(ref request)) {
578                 Connector.ReturnMessages.Add("Unable to Execute All Databases Request : Identity Header could not be added!");
579                 Connector.hasError = true;  //throws error
580                 return false;
581             }
582 
583             //test with encryption
584             StringBuilder sb = new StringBuilder();
585 
586             sb.AppendLine("UserName: " + Connection.UserName);
587             sb.AppendLine("ServerName: " + ServerName);
588            
589 
590             sb.AppendLine("Connecting via XPagesAPI");
591 
592             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
593             string myString = Encoding.UTF8.GetString(b);
594             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
595             if (EncodedEncryptedContent.Initialize()) {
596                 Stream requestStream = null;
597                 try {
598                     requestStream = request.GetRequestStream();
599                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8)) {
600                         writer.Write(EncodedEncryptedContent.EncodedContent);
601                     }
602                 } catch (Exception e) {
603                     Connector.ReturnMessages.Add("Unable to execute the All Databases Request : " + Common.GetErrorInfo(e));
604                     Connector.hasError = true;  //throws exception
605                     return false;
606                 } finally {
607                     //if (requestStream != null) {
608                     //    requestStream.Dispose();
609                     //}
610                 }
611             } else {
612                 Connector.ReturnMessages.Add("Executing All Databases Request - Unable to encode/encrypt the content of the request!");
613                 Connector.hasError = true; //throws exception
614                 return false;
615             }
616 
617             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
618 
619             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
620                 Stream responseStream = null;
621                 try {
622                     responseStream = request.GetResponse().GetResponseStream();
623                     using (StreamReader reader = new StreamReader(responseStream, true)) {
624                         result = reader.ReadToEnd();
625                     }
626                 } catch (Exception e) {
627                     Connector.ReturnMessages.Add("Unable to execute the All Databases Request : " + Common.GetErrorInfo(e));
628                     Connector.hasError = true;  //throws exception
629                     return false;
630                 } finally {
631                     //if (responseStream != null) {
632                     //    responseStream.Dispose();
633                     //}
634                 }
635 
636                 if (result != null) {
637                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
638                     if (DecodeDecryptedContent.Initialize()) {
639                         ArrayList arList = new ArrayList();
640 
641                         string[] ar = null;
642                         bool isError = false;
643 
644                         string message = "";
645                         string details = "";
646                         string value = "";
647 
648                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
649                         arList.AddRange(ar);
650                         DatabaseObject dbObj = null;
651                         foreach (string str in arList) {
652                             //YES/NO
653                             if (str.StartsWith("Error: ")) {
654                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase)) {
655                                     isError = true;
656                                 }
657                             }
658                             if (str.StartsWith("Message: ")) {
659                                 message = str.Replace("Message: ", "");
660                             }
661                             if (str.StartsWith("Details: ")) {
662                                 details = str.Replace("Details: ", "");
663                             }
664 
665                             if (str.StartsWith("DatabaseObject: ")) {
666                                 value = str.Replace("DatabaseObject: ", "");
667                                 if (!string.IsNullOrEmpty(value)) {
668                                     //here we have a line with all database properties separated by 
669                                     // FilePathServerNameReplicationIDTitleTemplateSizeURL
670                                     dbObj = new DatabaseObject(sObj, value);
671                                     if (dbObj.IsInitialized) {
672                                         if (sObj.Databases == null) {
673                                             sObj.Databases = new SortedDictionary<string, DatabaseObject>();
674                                         }
675                                         if (sObj.Databases.ContainsKey(dbObj.FilePath)) {
676                                             sObj.Databases.Remove(dbObj.FilePath);
677                                         }
678                                         sObj.Databases.Add(dbObj.FilePath, dbObj);
679                                     } else {
680                                         // Connector error response updated in constructor method - can trigger exception
681                                         Connector.ReturnMessages.Add("Error retrieving database object : " + value);
682                                     }
683                                 }
684                             }
685                         }
686                         if (isError) {
687                             Connector.ReturnMessages.Add(message);
688                             Connector.ReturnMessages.Add(details);
689                             Connector.hasError = true; //throws exception
690                             return false;
691                         } else {
692                             Connector.ReturnMessages.Add(message);
693                             Connector.ReturnMessages.Add(details);
694                             Connector.hasError = false; //SUCCESS
695                         }
696 
697                     } else {
698                         Connector.ReturnMessages.Add("Executing All Databases Request - Unable to decode/decrypt the content of the response!");
699                         Connector.hasError = true;  //throws exception
700                         return false;
701                     }
702                 } else {
703                     //no response from server
704                     Connector.ReturnMessages.Add("Executing All Databases Request -  Unable to get response from XPages!");
705                     Connector.hasError = true;  //throws exception
706                     return false;
707 
708                 }
709                 return true;
710             } else {
711                 Connector.ReturnMessages.Add("Executing All Databases Request -  Unable to get the Database - Authentication Issue");
712                 Connector.hasError = true;  //throws exception
713                 return false;
714             }
715 
716         } catch (Exception ex) {
717             if (ex.GetType() == typeof(WebException)) {
718                 WebException webex;
719                 webex = (WebException)ex;
720                 if (webex.Response != null) {
721                     Stream stream = webex.Response.GetResponseStream();
722                     using (StreamReader reader = new StreamReader(stream)) {
723                         string errorResp = reader.ReadToEnd();
724                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
725                         Connector.ReturnMessages.Add("Unable to Execute the All Databases Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
726                         Connector.hasError = true;  //throws exception
727                         return false;
728                     }
729 
730                 } else {
731                     Connector.ReturnMessages.Add("Unable to Execute the All Databases Request : " + Common.GetErrorInfo(ex));
732                     Connector.hasError = true;  //throws exception
733                     return false;
734                 }
735             } else {
736                 Connector.ReturnMessages.Add("Unable to Execute the All Databases Request : " + Common.GetErrorInfo(ex));
737                 Connector.hasError = true;  //throws exception
738                 return false;
739             }
740         }
741     }
