743     protected internal bool ExecuteDocumentRequest(string WebServiceURL, string Unid, string searchField, string searchValue, string formula, DatabaseObject dbObj, DocumentObject docObj) {
744         HttpWebRequest request = null;
745 
746         try {
747             if (!isInitialized) {
748                 Connector.ReturnMessages.Add("Unable to execute the Document Request - Not Initialized/Connected To Server");
749                 Connector.hasError = true;
750                 return false;
751             }
752 
753             // ServicePointManager.Expect100Continue = false;
754 
755             //create an HTTP request
756 
757             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Document");
758 
759             request.CookieContainer = this.AuthenticationCookie;
760             //  request.KeepAlive = True
761             request.AllowAutoRedirect = true;
762 
763             request.Method = WebRequestMethods.Http.Post;   //"POST"
764             request.ContentType = "application/jpi; charset=utf-8"; //application/json
765                                                                     // Set the request stream
766             string result;
767 
768             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
769             if (!AddIdentityHeader(ref request)) {
770                 Connector.ReturnMessages.Add("Unable to Execute Document Request : Identity Header could not be added!");
771                 Connector.hasError = true;  //throws error
772                 return false;
773             }
774 
775             //test with encryption
776             StringBuilder sb = new StringBuilder();
777 
778             sb.AppendLine("UserName: " + Connection.UserName);
779             sb.AppendLine("ServerName: " + docObj.Database.ServerName);
780             sb.AppendLine("FilePath: " + docObj.Database.FilePath);
781             sb.AppendLine("ReplicationID: " + docObj.Database.ReplicationID);
782             sb.AppendLine("UniversalID: " + Unid);
783             sb.AppendLine("SearchField: " + searchField);
784             sb.AppendLine("SearchValue: " + searchValue);
785             sb.AppendLine("Formula: " + formula);
786             sb.AppendLine("Connecting via XPagesAPI");
787 
788             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
789             string myString = Encoding.UTF8.GetString(b);
790             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
791             if (EncodedEncryptedContent.Initialize()) {
792                 Stream requestStream = null;
793                 try {
794                     requestStream = request.GetRequestStream();
795                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8)) {
796                         writer.Write(EncodedEncryptedContent.EncodedContent);
797                     }
798                 } catch (Exception e) {
799                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
800                     Connector.hasError = true;  //throws exception
801                     return false;
802                 } finally {
803                     //if (requestStream != null) {
804                     //    requestStream.Dispose();
805                     //}
806                 }
807             } else {
808                 Connector.ReturnMessages.Add("Executing Document Request - Unable to encode/encrypt the content of the request!");
809                 Connector.hasError = true; //throws exception
810                 return false;
811             }
812 
813             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
814 
815             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
816                 Stream responseStream = null;
817                 try {
818                     responseStream = request.GetResponse().GetResponseStream();
819                     using (StreamReader reader = new StreamReader(responseStream, true)) {
820                         result = reader.ReadToEnd();
821                     }
822                 } catch (Exception e) {
823                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
824                     Connector.hasError = true;  //throws exception
825                     return false;
826                 } finally {
827                     //if (responseStream != null) {
828                     //    responseStream.Dispose();
829                     //}
830                 }
831 
832                 if (result != null) {
833                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
834                     if (DecodeDecryptedContent.Initialize()) {
835                         ArrayList arList = new ArrayList();
836 
837                         string[] ar = null;
838                         bool isError = false;
839 
840                         string message = "";
841                         string details = "";
842                         string value = "";
843                         string unid = "";
844                         string noteId = "";
845                         string form = "";
846                         string size = "";
847                         string url = "";
848                         string created = "";
849                         string modified = "";
850 
851                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
852                         arList.AddRange(ar);
853 
854                         foreach (string str in arList) {
855                             //YES/NO
856                             if (str.StartsWith("Error: ")) {
857                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase)) {
858                                     isError = true;
859                                 }
860                             }
861                             if (str.StartsWith("Message: ")) {
862                                 message = str.Replace("Message: ", "");
863                             }
864                             if (str.StartsWith("Details: ")) {
865                                 details = str.Replace("Details: ", "");
866                             }
867 
868                             if (str.StartsWith("NoteID: ")) {
869                                 value = str.Replace("NoteID: ", "");
870                                 if (!string.IsNullOrEmpty(value)) {
871                                     noteId = value;
872                                 }
873                             }
874                             if (str.StartsWith("Unid: ")) {
875                                 value = str.Replace("Unid: ", "");
876                                 if (!string.IsNullOrEmpty(value)) {
877                                     unid = value;
878                                 }
879                             }
880                             if (str.StartsWith("Form: ")) {
881                                 value = str.Replace("Form: ", "");
882                                 if (!string.IsNullOrEmpty(value)) {
883                                     form = value;
884                                 }
885                             }
886 
887                             if (str.StartsWith("Size: ")) {
888                                 value = str.Replace("Size: ", "");
889                                 if (!string.IsNullOrEmpty(value)) {
890                                     size = value;
891                                 }
892                             }
893 
894                             if (str.StartsWith("URL: ")) {
895                                 value = str.Replace("URL: ", "");
896                                 if (!string.IsNullOrEmpty(value)) {
897                                     url = value;
898                                 }
899                             }
900 
901 
902                             if (str.StartsWith("Created: ")) {
903                                 value = str.Replace("Created: ", "");
904                                 if (!string.IsNullOrEmpty(value)) {
905                                     created = value;
906                                 }
907                             }
908 
909 
910                             if (str.StartsWith("Modified: ")) {
911                                 value = str.Replace("Modified: ", "");
912                                 if (!string.IsNullOrEmpty(value)) {
913                                     modified = value;
914                                 }
915                             }
916                         }
917 
918                         if (isError) {
919                             Connector.ReturnMessages.Add(message);
920                             Connector.ReturnMessages.Add(details);
921                             Connector.hasError = true; //throws exception
922                             return false;
923                         } else {
924 
925                             // add doc to list
926                             if (dbObj.Documents == null) {
927                                 dbObj.Documents = new Dictionary<string, DocumentObject>();
928                             }
929                             if (!string.IsNullOrEmpty(unid)) {
930                                 //docObj = new DocumentObject(dbObj, unid);
931                                 docObj.UniversalID = unid;
932                                 docObj.NoteID = noteId;
933                                 docObj.Size = size;
934                                 docObj.Url = url;
935                                 docObj.Form = form;
936                                 docObj.DateCreated = created;
937                                 docObj.DateModified = modified;
938                                 if (dbObj.Documents.ContainsKey(unid)) {
939                                     dbObj.Documents.Remove(unid);
940                                 }
941                                 dbObj.Documents.Add(unid, docObj);
942                                 Connector.ReturnMessages.Add(message);
943                                 Connector.ReturnMessages.Add(details);
944                                 Connector.hasError = false; //SUCCESS
945                             } else {
946                                 // unid not found??
947                                 Connector.ReturnMessages.Add(message);
948                                 Connector.ReturnMessages.Add(details);
949                                 Connector.ReturnMessages.Add("Universal ID not found!");
950                                 Connector.hasError = true; // throws exception
951                             }
952 
953                         }
954 
955                     } else {
956                         Connector.ReturnMessages.Add("Executing Document Request - Unable to decode/decrypt the content of the response!");
957                         Connector.hasError = true;  //throws exception
958                         return false;
959                     }
960                 } else {
961                     //no response from server
962                     Connector.ReturnMessages.Add("Executing Document Request -  Unable to get response from XPages!");
963                     Connector.hasError = true;  //throws exception
964                     return false;
965 
966                 }
967                 return true;
968             } else {
969                 Connector.ReturnMessages.Add("Executing Document Request -  Unable to get the Document - Authentication Issue");
970                 Connector.hasError = true;  //throws exception
971                 return false;
972             }
973 
974         } catch (Exception ex) {
975             if (ex.GetType() == typeof(WebException)) {
976                 WebException webex;
977                 webex = (WebException)ex;
978                 if (webex.Response != null) {
979                     Stream stream = webex.Response.GetResponseStream();
980                     using (StreamReader reader = new StreamReader(stream)) {
981                         string errorResp = reader.ReadToEnd();
982                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
983                         Connector.ReturnMessages.Add("Unable to Execute the Document Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
984                         Connector.hasError = true;  //throws exception
985                         return false;
986                     }
987 
988                 } else {
989                     Connector.ReturnMessages.Add("Unable to Execute the Document Request : " + Common.GetErrorInfo(ex));
990                     Connector.hasError = true;  //throws exception
991                     return false;
992                 }
993             } else {
994                 Connector.ReturnMessages.Add("Unable to Execute the Document Request : " + Common.GetErrorInfo(ex));
995                 Connector.hasError = true;  //throws exception
996                 return false;
997             }
998         }
999     }
