1118                                     isError = true;
1119                                 }
1120                             }
1121                             if (str.StartsWith("Message: ")) {
1122                                 message = str.Replace("Message: ", "");
1123                             }
1124                             if (str.StartsWith("Details: ")) {
1125                                 details = str.Replace("Details: ", "");
1126                             }
1127 
