1142                                 if (!string.IsNullOrEmpty(value)) {
1143                                     form = value;
1144                                 }
1145                             }
1146 
1147                             if (str.StartsWith("Size: ")) {
1148                                 value = str.Replace("Size: ", "");
1149                                 if (!string.IsNullOrEmpty(value)) {
1150                                     size = value;
1151                                 }
1152                             }
1153 
1154                             if (str.StartsWith("URL: ")) {
1155                                 value = str.Replace("URL: ", "");
1156                                 if (!string.IsNullOrEmpty(value)) {
1157                                     url = value;
1158                                 }
1159                             }
1160 
1161 
1162                             if (str.StartsWith("Created: ")) {
